import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useClientView } from '../context/ClientViewContext.jsx';
import Layout from '../components/Layout/Layout';
import CategoryManagementModal from '../components/CategoryManagementModal';
import api from '../utils/api';
import { formatINR } from '../utils/currency';
import {
    Package,
    Search,
    Plus,
    Edit2,
    Trash2,
    X,
    Upload,
    Download,
    FolderTree
} from 'lucide-react';
import toast from 'react-hot-toast';

const Inventory = () => {
    const navigate = useNavigate();
    const { isAdmin } = useAuth();
    const { isClientView } = useClientView();
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [categoryFilter, setCategoryFilter] = useState('All Categories');
    const [loading, setLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [showCategoryModal, setShowCategoryModal] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);

    const [categories, setCategories] = useState(['All Categories']);

    useEffect(() => {
        loadProducts();
        loadCategories();
    }, []);

    const loadCategories = async () => {
        try {
            const { data } = await api.get('/categories?parent=null');
            setCategories(['All Categories', ...data.map(c => c.name)]);
        } catch (error) {
            console.error('Failed to load categories');
        }
    };

    useEffect(() => {
        let filtered = products;

        // Search filter
        if (searchQuery) {
            filtered = filtered.filter(p =>
                p.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                p.sku?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                p.imei1?.includes(searchQuery) ||
                p.imei2?.includes(searchQuery) ||
                p.serialNumber?.includes(searchQuery)
            );
        }

        // Category filter
        if (categoryFilter !== 'All Categories') {
            filtered = filtered.filter(p => p.category === categoryFilter);
        }

        setFilteredProducts(filtered);
    }, [searchQuery, categoryFilter, products]);

    const loadProducts = async () => {
        try {
            setLoading(true);
            const { data } = await api.get('/products');
            setProducts(data);
            setFilteredProducts(data);
            setLoading(false);
        } catch (error) {
            console.error('Failed to load products:', error);
            toast.error('Failed to load products');
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Are you sure you want to delete this product?')) return;

        try {
            await api.delete(`/products/${id}`);
            toast.success('Product deleted successfully');
            loadProducts();
        } catch (error) {
            toast.error('Failed to delete product');
        }
    };

    const handleEdit = (product) => {
        setEditingProduct(product);
        setShowModal(true);
    };

    const handleAdd = () => {
        setEditingProduct(null);
        setShowModal(true);
    };

    const handleExport = () => {
        // Define headers
        const headers = ['Name', 'SKU', 'Category', 'Sub-Category', 'Selling Price', 'Stock', 'Min Stock Alert'];
        if (!isClientView) {
            headers.splice(4, 0, 'Cost Price');
        }

        // Map data to rows
        const rows = filteredProducts.map(p => {
            const row = [
                `"${p.name.replace(/"/g, '""')}"`, // Escape quotes
                `"${p.sku || ''}"`,
                `"${p.category || ''}"`,
                `"${p.subCategory || ''}"`,
                p.sellingPrice || 0,
                p.stock || 0,
                p.minStockAlert || 5
            ];

            if (!isClientView) {
                row.splice(4, 0, p.costPrice || 0);
            }

            return row;
        });

        // Combine headers and rows
        const csvContent = [
            headers.join(','),
            ...rows.map(r => r.join(','))
        ].join('\n');

        // Create download link
        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        const link = document.createElement('a');
        if (link.download !== undefined) {
            const url = URL.createObjectURL(blob);
            link.setAttribute('href', url);
            link.setAttribute('download', `inventory_export_${new Date().toISOString().split('T')[0]}.csv`);
            link.style.visibility = 'hidden';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
        }
    };

    const getStockColor = (stock) => {
        if (stock <= 5) return 'text-red-600 bg-red-50';
        if (stock <= 10) return 'text-yellow-600 bg-yellow-50';
        return 'text-green-600 bg-green-50';
    };

    return (
        <Layout>
            <div className="p-6">
                {/* Header */}
                <div className="mb-6">
                    <h1 className="text-3xl font-bold text-gray-900">Inventory Management</h1>
                    <p className="text-gray-600 mt-1">Manage your product catalog</p>
                </div>

                {/* Toolbar */}
                <div className="bg-white rounded-xl shadow-sm p-4 mb-6">
                    <div className="flex flex-col lg:flex-row items-center gap-4">
                        {/* Search */}
                        <div className="relative flex-1 w-full">
                            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input
                                type="text"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                placeholder="Search by name or SKU..."
                                className="w-full pl-12 pr-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                            />
                        </div>

                        {/* Category Filter */}
                        <select
                            value={categoryFilter}
                            onChange={(e) => setCategoryFilter(e.target.value)}
                            className="px-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 w-full lg:w-auto"
                        >
                            {categories.map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>

                        {/* Sub-Category Filter (Placeholder) */}
                        <select
                            className="px-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 w-full lg:w-auto"
                        >
                            <option>All Sub-Categories</option>
                        </select>

                        {/* Action Buttons */}
                        <div className="flex items-center gap-2 w-full lg:w-auto">
                            <button
                                onClick={() => setShowCategoryModal(true)}
                                className="flex items-center space-x-2 px-4 py-2.5 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                            >
                                <FolderTree className="w-4 h-4" />
                                <span className="text-sm font-medium">Manage Categories</span>
                            </button>

                            <button className="flex items-center space-x-2 px-4 py-2.5 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors">
                                <Upload className="w-4 h-4" />
                                <span className="text-sm font-medium">Import CSV</span>
                            </button>

                            <button
                                onClick={handleExport}
                                className="flex items-center space-x-2 px-4 py-2.5 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                            >
                                <Download className="w-4 h-4" />
                                <span className="text-sm font-medium">Export CSV</span>
                            </button>

                            <button
                                onClick={handleAdd}
                                className="flex items-center space-x-2 px-4 py-2.5 bg-gray-900 text-white rounded-lg hover:bg-gray-800 transition-colors"
                            >
                                <Plus className="w-4 h-4" />
                                <span className="text-sm font-medium">Add Product</span>
                            </button>
                        </div>
                    </div>
                </div>

                {/* Products Table */}
                <div className="bg-white rounded-xl shadow-sm overflow-hidden">
                    {loading ? (
                        <div className="text-center py-12">
                            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
                        </div>
                    ) : filteredProducts.length === 0 ? (
                        <div className="text-center py-16 text-gray-500">
                            <Package className="w-16 h-16 mx-auto mb-4 opacity-50" />
                            <p className="text-lg">No products found. Add your first product to get started.</p>
                        </div>
                    ) : (
                        <div className="overflow-x-auto">
                            <table className="w-full">
                                <thead className="bg-gray-50 border-b border-gray-200">
                                    <tr>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Image
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Product Name
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            SKU
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Category
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Sub-Category
                                        </th>
                                        <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Selling Price
                                        </th>
                                        {isAdmin() && !isClientView && (
                                            <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                Cost Price
                                            </th>
                                        )}
                                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Stock
                                        </th>
                                        <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Actions
                                        </th>
                                    </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                    {filteredProducts.map((product) => (
                                        <tr key={product._id} className="hover:bg-gray-50 transition-colors">
                                            <td className="px-6 py-4">
                                                {product.image ? (
                                                    <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-12 h-12 rounded-lg object-cover border border-gray-200"
                                                    />
                                                ) : (
                                                    <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center">
                                                        <Package className="w-6 h-6 text-gray-400" />
                                                    </div>
                                                )}
                                            </td>
                                            <td className="px-6 py-4">
                                                <div>
                                                    <p className="font-medium text-gray-900">{product.name}</p>
                                                    {product.imei1 && (
                                                        <p className="text-xs text-gray-500">IMEI: {product.imei1}</p>
                                                    )}
                                                    {product.serialNumber && (
                                                        <p className="text-xs text-gray-500">S/N: {product.serialNumber}</p>
                                                    )}
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-600">
                                                {product.sku || '-'}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-900">
                                                {product.category}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-600">
                                                -
                                            </td>
                                            <td className="px-6 py-4 text-right text-sm font-semibold text-gray-900 rupee">
                                                {formatINR(product.sellingPrice)}
                                            </td>
                                            {isAdmin() && !isClientView && (
                                                <td className="px-6 py-4 text-right text-sm text-gray-600 rupee">
                                                    {formatINR(product.costPrice)}
                                                </td>
                                            )}
                                            <td className="px-6 py-4 text-center">
                                                <span className={`inline-block px-3 py-1 rounded-full text-sm font-semibold ${getStockColor(product.stock)}`}>
                                                    {product.stock}
                                                </span>
                                            </td>
                                            <td className="px-6 py-4 text-center">
                                                <div className="flex items-center justify-center space-x-2">
                                                    <button
                                                        onClick={() => handleEdit(product)}
                                                        className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                                                        title="Edit"
                                                    >
                                                        <Edit2 className="w-4 h-4" />
                                                    </button>
                                                    <button
                                                        onClick={() => handleDelete(product._id)}
                                                        className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                                                        title="Delete"
                                                    >
                                                        <Trash2 className="w-4 h-4" />
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>

            {/* Add/Edit Product Modal */}
            {showModal && (
                <ProductModal
                    product={editingProduct}
                    onClose={() => {
                        setShowModal(false);
                        setEditingProduct(null);
                    }}
                    onSuccess={() => {
                        setShowModal(false);
                        setEditingProduct(null);
                        loadProducts();
                    }}
                />
            )}

            {/* Category Management Modal */}
            {showCategoryModal && (
                <CategoryManagementModal
                    onClose={() => {
                        setShowCategoryModal(false);
                        loadCategories();
                    }}
                />
            )}
        </Layout>
    );
};

// Product Modal Component
const ProductModal = ({ product, onClose, onSuccess }) => {
    const [formData, setFormData] = useState({
        name: product?.name || '',
        sku: product?.sku || '',
        category: product?.category || '',
        subCategory: product?.subCategory || '',
        costPrice: product?.costPrice || '',
        margin: product?.margin || '',
        sellingPrice: product?.sellingPrice || '',
        stock: product?.stock || '',
        minStockAlert: product?.minStockAlert || 5, // Default 5
        gstPercent: product?.gstPercent || 18,
        imei1: product?.imei1 || '',
        imei2: product?.imei2 || '',
        serialNumber: product?.serialNumber || '',
        image: product?.image || '',
    });

    const [categories, setCategories] = useState([]);
    const [subCategories, setSubCategories] = useState([]);
    const [loading, setLoading] = useState(false);

    // Load categories on mount
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const { data } = await api.get('/categories?parent=null');
                setCategories(data);
            } catch (error) {
                console.error('Failed to load categories');
            }
        };
        fetchCategories();
    }, []);

    // Load sub-categories when category changes
    useEffect(() => {
        const fetchSubCategories = async () => {
            if (!formData.category) {
                setSubCategories([]);
                return;
            }

            // Find category ID
            const selectedCat = categories.find(c => c.name === formData.category);
            if (!selectedCat) return;

            try {
                const { data } = await api.get(`/categories/${selectedCat._id}`);
                setSubCategories(data.subCategories || []);
            } catch (error) {
                console.error('Failed to load sub-categories');
            }
        };
        fetchSubCategories();
    }, [formData.category, categories]);

    // Auto-calculate logic
    useEffect(() => {
        if (formData.costPrice && formData.margin) {
            const cp = parseFloat(formData.costPrice);
            const margin = parseFloat(formData.margin);
            const sp = cp + (cp * margin / 100);
            setFormData(prev => ({ ...prev, sellingPrice: sp.toFixed(2) }));
        }
    }, [formData.costPrice, formData.margin]);

    const handleSellingPriceChange = (e) => {
        const sp = parseFloat(e.target.value);
        setFormData(prev => ({ ...prev, sellingPrice: e.target.value }));

        if (formData.costPrice && sp) {
            const cp = parseFloat(formData.costPrice);
            const margin = ((sp - cp) / cp) * 100;
            setFormData(prev => ({ ...prev, margin: margin.toFixed(2) }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            if (product) {
                await api.put(`/products/${product._id}`, formData);
                toast.success('Product updated successfully');
            } else {
                await api.post('/products', formData);
                toast.success('Product added successfully');
            }
            onSuccess();
        } catch (error) {
            toast.error(error.response?.data?.message || 'Failed to save product');
        } finally {
            setLoading(false);
        }
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (!file) return;

        // Check file size (max 5MB)
        if (file.size > 5 * 1024 * 1024) {
            toast.error('Image size should be less than 5MB');
            return;
        }

        const reader = new FileReader();
        reader.onloadend = () => {
            setFormData({ ...formData, image: reader.result });
        };
        reader.readAsDataURL(file);
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-2xl shadow-xl max-w-3xl w-full max-h-[90vh] overflow-y-auto">
                <div className="px-8 py-6 border-b border-gray-100 flex items-center justify-between sticky top-0 bg-white z-10">
                    <div>
                        <h2 className="text-2xl font-bold text-gray-900">
                            {product ? 'Edit Product' : 'Add New Product'}
                        </h2>
                        <p className="text-sm text-gray-500 mt-1">
                            Fill in the product details to add it to your inventory.
                        </p>
                    </div>
                    <button
                        onClick={onClose}
                        className="p-2 hover:bg-gray-100 rounded-full transition-colors text-gray-400 hover:text-gray-600"
                    >
                        <X className="w-6 h-6" />
                    </button>
                </div>

                <form onSubmit={handleSubmit} className="p-8 space-y-6">
                    {/* Basic Details */}
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-semibold text-gray-700 mb-1.5">Product Name</label>
                            <input
                                type="text"
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none"
                                placeholder="Enter product name"
                                required
                            />
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">SKU</label>
                                <input
                                    type="text"
                                    value={formData.sku}
                                    onChange={(e) => setFormData({ ...formData, sku: e.target.value })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-gray-50"
                                    placeholder="Auto-generated if empty"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Category</label>
                                <select
                                    value={formData.category}
                                    onChange={(e) => setFormData({ ...formData, category: e.target.value, subCategory: '' })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-white"
                                    required
                                >
                                    <option value="">Select category</option>
                                    {categories.map(cat => (
                                        <option key={cat._id} value={cat.name}>{cat.name}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Sub-Category</label>
                                <select
                                    value={formData.subCategory}
                                    onChange={(e) => setFormData({ ...formData, subCategory: e.target.value })}
                                    className={`w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-white ${!formData.category ? 'opacity-50 cursor-not-allowed' : ''}`}
                                    disabled={!formData.category}
                                >
                                    <option value="">Select sub-category</option>
                                    {subCategories.map(sub => (
                                        <option key={sub._id} value={sub.name}>{sub.name}</option>
                                    ))}
                                </select>
                            </div>
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Cost Price (Γé╣)</label>
                                <input
                                    type="number"
                                    value={formData.costPrice}
                                    onChange={(e) => setFormData({ ...formData, costPrice: e.target.value })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-gray-50"
                                    placeholder="Enter cost price"
                                    required
                                    min="0"
                                    step="0.01"
                                />
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Margin (%)</label>
                                <div className="relative">
                                    <input
                                        type="number"
                                        value={formData.margin}
                                        onChange={(e) => setFormData({ ...formData, margin: e.target.value })}
                                        className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-gray-50"
                                        placeholder="e.g., 20"
                                        step="0.1"
                                    />
                                    <span className="absolute right-4 top-1/2 -translate-y-1/2 text-gray-400 font-medium">%</span>
                                </div>
                            </div>
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Selling Price (Γé╣)</label>
                                <input
                                    type="number"
                                    value={formData.sellingPrice}
                                    onChange={handleSellingPriceChange}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-gray-50"
                                    placeholder="Calculated automatically"
                                    required
                                    min="0"
                                    step="0.01"
                                />
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Current Stock</label>
                                <input
                                    type="number"
                                    value={formData.stock}
                                    onChange={(e) => setFormData({ ...formData, stock: e.target.value })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-gray-50"
                                    required
                                    min="0"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Min Stock Alert Level</label>
                                <input
                                    type="number"
                                    value={formData.minStockAlert}
                                    onChange={(e) => setFormData({ ...formData, minStockAlert: e.target.value })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-gray-50"
                                    min="0"
                                />
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">GST Rate (%)</label>
                                <select
                                    value={formData.gstPercent}
                                    onChange={(e) => setFormData({ ...formData, gstPercent: e.target.value })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-white"
                                >
                                    <option value={0}>0% (Nil Rated)</option>
                                    <option value={5}>5%</option>
                                    <option value={12}>12%</option>
                                    <option value={18}>18% (Standard)</option>
                                    <option value={28}>28% (Luxury)</option>
                                </select>
                            </div>
                        </div>

                        {/* Category Specific Fields */}
                        {formData.category === 'Phone' && (
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 p-4 bg-gray-50 rounded-xl border border-gray-200">
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-1.5">IMEI 1</label>
                                    <input
                                        type="text"
                                        value={formData.imei1}
                                        onChange={(e) => setFormData({ ...formData, imei1: e.target.value })}
                                        className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-white"
                                        placeholder="Enter IMEI 1"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-1.5">IMEI 2</label>
                                    <input
                                        type="text"
                                        value={formData.imei2}
                                        onChange={(e) => setFormData({ ...formData, imei2: e.target.value })}
                                        className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-white"
                                        placeholder="Enter IMEI 2"
                                    />
                                </div>
                            </div>
                        )}

                        {(formData.category === 'Watch' || formData.category === 'Audio') && (
                            <div className="p-4 bg-gray-50 rounded-xl border border-gray-200">
                                <label className="block text-sm font-semibold text-gray-700 mb-1.5">Serial Number</label>
                                <input
                                    type="text"
                                    value={formData.serialNumber}
                                    onChange={(e) => setFormData({ ...formData, serialNumber: e.target.value })}
                                    className="w-full px-4 py-2.5 rounded-lg border border-gray-300 focus:ring-2 focus:ring-gray-900 focus:border-transparent transition-all outline-none bg-white"
                                    placeholder="Enter Serial Number"
                                />
                            </div>
                        )}

                        {/* Image Upload */}
                        <div>
                            <label className="block text-sm font-semibold text-gray-700 mb-2">Product Image (Optional)</label>
                            <div className="flex items-center gap-4">
                                <div className={`w-24 h-24 rounded-xl border-2 border-dashed border-gray-300 flex items-center justify-center overflow-hidden bg-gray-50 ${!formData.image ? 'p-4' : ''}`}>
                                    {formData.image ? (
                                        <div className="relative w-full h-full group">
                                            <img src={formData.image} alt="Preview" className="w-full h-full object-cover" />
                                            <button
                                                type="button"
                                                onClick={() => setFormData({ ...formData, image: '' })}
                                                className="absolute top-1 right-1 bg-red-500 text-white rounded-full p-1 shadow-md opacity-0 group-hover:opacity-100 transition-opacity"
                                                title="Remove Image"
                                            >
                                                <X className="w-3 h-3" />
                                            </button>
                                        </div>
                                    ) : (
                                        <div className="text-gray-400">
                                            <svg className="w-8 h-8 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                                            </svg>
                                        </div>
                                    )}
                                </div>
                                <div>
                                    <label className="cursor-pointer bg-blue-600 hover:bg-blue-700 text-white px-4 py-2.5 rounded-lg font-medium transition-colors inline-flex items-center gap-2">
                                        <Upload className="w-4 h-4" />
                                        <span>Upload Image</span>
                                        <input
                                            type="file"
                                            onChange={handleImageUpload}
                                            accept="image/*"
                                            className="hidden"
                                        />
                                    </label>
                                    <p className="text-xs text-gray-500 mt-2">
                                        PNG, JPG, WEBP (max 5MB)<br />
                                        Auto-compressed to &lt;50KB for storage
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Footer Actions */}
                    <div className="flex items-center justify-end space-x-4 pt-6 mt-6 border-t border-gray-100">
                        <button
                            type="button"
                            onClick={onClose}
                            className="px-6 py-2.5 border border-gray-300 rounded-lg text-gray-700 font-medium hover:bg-gray-50 transition-colors"
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            disabled={loading}
                            className="px-6 py-2.5 bg-gray-900 text-white rounded-lg font-medium hover:bg-gray-800 transition-colors disabled:opacity-50 flex items-center gap-2"
                        >
                            {loading ? (
                                <>
                                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                                    <span>Saving...</span>
                                </>
                            ) : (
                                <span>{product ? 'Update Product' : 'Create Product'}</span>
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Inventory;

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout/Layout';
import api from '../utils/api';
import { formatINR } from '../utils/currency';
import { calculateLineTotal, calculateInvoiceSummary } from '../utils/gstCalculator';
import { useSettings } from '../context/SettingsContext';
import {
    Search,
    Plus,
    Trash2,
    Calendar,
    User,
    ChevronDown,
    ChevronUp,
    Store,
    Save,
    Printer,
    Share2
} from 'lucide-react';
import toast from 'react-hot-toast';
import { QRCodeSVG } from 'qrcode.react';

const InvoiceCreator = () => {
    const navigate = useNavigate();
    const { settings } = useSettings();
    const [loading, setLoading] = useState(false);

    // Data Sources
    const [products, setProducts] = useState([]);
    const [customers, setCustomers] = useState([]);

    // UI State
    const [showProductSearch, setShowProductSearch] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [showCompanyDetails, setShowCompanyDetails] = useState(true);
    const [sellerDetails, setSellerDetails] = useState({
        storeName: '',
        tagline: '',
        address: '',
        phone: '',
        email: '',
        gstin: '',
        bankDetails: '',
        upiId: '',
        website: ''
    });

    // Invoice State
    const [invoiceSettings, setInvoiceSettings] = useState({
        title: 'Tax Invoice',
        date: new Date().toISOString().split('T')[0],
        invoiceNo: 'Auto-generated'
    });

    const [customerInfo, setCustomerInfo] = useState({
        _id: null,
        name: '',
        phone: '',
        email: '',
        gstin: '',
        address: ''
    });

    const [items, setItems] = useState([]);
    const [newItemConfig, setNewItemConfig] = useState({
        selectedProduct: null,
        qty: 1
    });

    const [payment, setPayment] = useState({
        cash: 0,
        upi: 0,
        card: 0,
        bank: 0
    });

    // Load Initial Data
    useEffect(() => {
        loadProducts();
        loadCustomers();
    }, []);

    const loadProducts = async () => {
        try {
            const { data } = await api.get('/products');
            setProducts(data);
        } catch (error) {
            console.error('Failed to load products');
        }
    };

    const loadCustomers = async () => {
        try {
            const { data } = await api.get('/customers');
            setCustomers(data);
        } catch (error) {
            console.error('Failed to load customers');
        }
    };

    // Initialize Seller Details from Settings
    useEffect(() => {
        if (settings) {
            setSellerDetails({
                storeName: settings.storeName || settings.shopName || '',
                tagline: '', // Manually entered
                address: settings.address || '',
                phone: settings.phone || '',
                email: settings.email || '',
                gstin: settings.gstin || settings.gstNumber || '',
                bankDetails: (settings.bankName && settings.accountNumber)
                    ? `${settings.bankName}, A/c: ${settings.accountNumber}, IFSC: ${settings.ifscCode}`
                    : '',
                upiId: settings.upi || settings.upiId || '',
                website: settings.website || ''
            });
        }
    }, [settings]);

    // Product Search Logic
    useEffect(() => {
        if (searchQuery) {
            const lower = searchQuery.toLowerCase();
            const filtered = products.filter(p =>
                p.name.toLowerCase().includes(lower) ||
                p.category.toLowerCase().includes(lower) ||
                p.sku?.toLowerCase().includes(lower)
            );
            setFilteredProducts(filtered);
            setShowProductSearch(true);
        } else {
            setFilteredProducts([]);
            setShowProductSearch(false);
        }
    }, [searchQuery, products]);

    // Customer Selection
    const handleCustomerSelect = (customerId) => {
        if (!customerId) return;
        const customer = customers.find(c => c._id === customerId);
        if (customer) {
            setCustomerInfo({
                _id: customer._id,
                name: customer.name,
                phone: customer.phone,
                email: customer.email || '',
                gstin: customer.gstin || '',
                address: customer.address || ''
            });
        }
    };

    // Add Item Logic
    const addItem = (product = null, isCustom = false) => {
        const qty = parseInt(newItemConfig.qty) || 1;

        let newItem;
        if (isCustom) {
            newItem = {
                productId: null,
                name: '',
                isCustom: true,
                showImei: true, // Show fields by default for custom items
                quantity: 1,
                pricePerUnit: 0,
                gstPercent: 0,
                taxableValue: 0,
                gstAmount: 0,
                totalAmount: 0
            };
        } else if (product) {
            // Check if exists
            const existingIndex = items.findIndex(i => i.productId === product._id);
            if (existingIndex >= 0) {
                toast.success('Product already added. Adjusted quantity.');
                updateItem(existingIndex, 'quantity', items[existingIndex].quantity + qty);
                setSearchQuery('');
                return;
            }

            newItem = {
                productId: product._id,
                name: product.name,
                category: product.category, // Store category for IMEI check
                showImei: false, // Default to hidden for inventory unless auto-detected
                imei: '', // Init IMEI 1
                imei2: '', // Init IMEI 2
                serialNumber: '', // Init Serial
                isCustom: false,
                quantity: qty,
                pricePerUnit: product.sellingPrice,
                gstPercent: product.gstPercent,
                ...calculateLineTotal(qty, product.sellingPrice, product.gstPercent, true)
            };
        }

        if (newItem) {
            setItems([...items, newItem]);
            setSearchQuery('');
            setNewItemConfig({ selectedProduct: null, qty: 1 });
            if (!isCustom) setShowProductSearch(false);
        }
    };

    // Update Item
    const updateItem = (index, field, value) => {
        const updatedItems = [...items];
        const item = { ...updatedItems[index] };

        if (field === 'quantity') item.quantity = parseFloat(value) || 0;
        if (field === 'pricePerUnit') item.pricePerUnit = parseFloat(value) || 0;
        if (field === 'gstPercent') item.gstPercent = parseFloat(value) || 0;
        if (field === 'name') item.name = value;
        if (field === 'imei') item.imei = value;
        if (field === 'imei2') item.imei2 = value;
        if (field === 'serialNumber') item.serialNumber = value;
        if (field === 'showImei') item.showImei = value;

        // Recalculate
        const totals = calculateLineTotal(
            item.quantity,
            item.pricePerUnit,
            item.gstPercent,
            true // Inclusive tax calculation preferred for retail
        );

        updatedItems[index] = { ...item, ...totals };
        setItems(updatedItems);
    };

    const removeItem = (index) => {
        setItems(items.filter((_, i) => i !== index));
    };

    // Calculations
    const summary = calculateInvoiceSummary(items);
    const totalPaid = Object.values(payment).reduce((sum, val) => sum + (parseFloat(val) || 0), 0);
    const balanceDue = summary.grandTotal - totalPaid;
    const isFullyPaid = balanceDue <= 0.5; // Tolerance for float

    // Submit
    const handleSubmit = async () => {
        if (items.length === 0) {
            toast.error('Add items to invoice first');
            return;
        }

        if (!customerInfo.name) {
            toast.error('Customer name is required');
            return;
        }

        try {
            setLoading(true);
            const invoicePayload = {
                customerId: customerInfo._id || undefined, // FIXED: was 'customer', backend expects 'customerId'
                invoiceType: invoiceSettings.title,
                invoiceDate: invoiceSettings.date,
                items: items.map(item => ({
                    productId: item.productId, // FIXED: was 'product', backend expects 'productId'
                    itemName: item.name,
                    quantity: item.quantity,
                    pricePerUnit: item.pricePerUnit,
                    gstPercent: item.gstPercent,
                    isTaxInclusive: true, // All items are tax-inclusive in this UI
                    imei: item.imei, // Include IMEI 1
                    imei2: item.imei2, // Include IMEI 2
                    serialNumber: item.serialNumber // Include Serial
                })),
                payments: Object.entries(payment)
                    .filter(([_, amount]) => amount > 0)
                    .map(([mode, amount]) => ({
                        method: mode.charAt(0).toUpperCase() + mode.slice(1),
                        amount
                    })),
                customerName: customerInfo.name,
                customerPhone: customerInfo.phone,
                customerAddress: customerInfo.address,
                customerGstin: customerInfo.gstin,
                sellerDetails,
                notes: customerInfo.notes || ''
            };

            const { data } = await api.post('/invoices', invoicePayload);
            toast.success('Invoice Created Successfully!');
            navigate('/invoices');

        } catch (error) {
            console.error(error);
            toast.error(error.response?.data?.message || 'Failed to create invoice');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Layout>
            <div className="p-6 max-w-[1600px] mx-auto">
                <div className="mb-6">
                    <h1 className="text-3xl font-bold text-gray-900">Create New Invoice</h1>
                    <p className="text-gray-600">Generate a fully customizable invoice</p>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">
                    {/* LEFT SIDEBAR (3 cols) */}
                    <div className="lg:col-span-3 space-y-6">

                        {/* Invoice Settings */}
                        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-5">
                            <h3 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
                                <Printer className="w-4 h-4 text-blue-600" />
                                Invoice Settings
                            </h3>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Invoice Title</label>
                                    <select
                                        value={invoiceSettings.title}
                                        onChange={(e) => setInvoiceSettings({ ...invoiceSettings, title: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                                    >
                                        <option value="Tax Invoice">TAX INVOICE</option>
                                        <option value="Bill of Supply">BILL OF SUPPLY</option>
                                        <option value="Estimate">ESTIMATE / QUOTATION</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Invoice Date</label>
                                    <input
                                        type="date"
                                        value={invoiceSettings.date}
                                        onChange={(e) => setInvoiceSettings({ ...invoiceSettings, date: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                                    />
                                    <p className="text-[10px] text-gray-400 mt-1">Supports backdating</p>
                                </div>
                            </div>
                        </div>

                        {/* Customer Information */}
                        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-5">
                            <h3 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
                                <User className="w-4 h-4 text-blue-600" />
                                Customer Information
                            </h3>

                            {/* Search Existing */}
                            <div className="mb-4 relative">
                                <input
                                    type="text"
                                    placeholder="Search existing customer..."
                                    className="w-full pl-9 pr-3 py-2 border border-blue-100 bg-blue-50 rounded-lg text-sm focus:bg-white transition-colors"
                                    onChange={(e) => {
                                        const found = customers.find(c => c.name.toLowerCase().includes(e.target.value.toLowerCase()) || c.phone.includes(e.target.value));
                                        if (found) handleCustomerSelect(found._id);
                                    }}
                                />
                                <Search className="w-4 h-4 text-blue-400 absolute left-3 top-2.5" />
                            </div>

                            <div className="space-y-3">
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Customer Name *</label>
                                    <input
                                        type="text"
                                        value={customerInfo.name}
                                        onChange={(e) => setCustomerInfo({ ...customerInfo, name: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                                        placeholder="Enter customer name"
                                    />
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Mobile Number</label>
                                    <input
                                        type="text"
                                        value={customerInfo.phone}
                                        onChange={(e) => setCustomerInfo({ ...customerInfo, phone: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                                        placeholder="+91 98765 43210"
                                    />
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Email</label>
                                    <input
                                        type="email"
                                        value={customerInfo.email}
                                        onChange={(e) => setCustomerInfo({ ...customerInfo, email: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                                        placeholder="customer@email.com"
                                    />
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">GSTIN</label>
                                    <input
                                        type="text"
                                        value={customerInfo.gstin}
                                        onChange={(e) => setCustomerInfo({ ...customerInfo, gstin: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                                        placeholder="GSTIN (Optional)"
                                    />
                                </div>
                            </div>
                        </div>

                        {/* Company Details */}
                        <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                            <button
                                onClick={() => setShowCompanyDetails(!showCompanyDetails)}
                                className="w-full p-4 flex items-center justify-between bg-gray-50 hover:bg-gray-100 transition-colors"
                            >
                                <span className="font-semibold text-gray-800 flex items-center gap-2">
                                    <Store className="w-4 h-4 text-gray-600" />
                                    Company Details
                                </span>
                                {showCompanyDetails ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
                            </button>

                            {showCompanyDetails && (
                                <div className="p-5 border-t border-gray-100 flex flex-col gap-3">
                                    <p className="text-[10px] text-gray-500 italic">These details are pre-filled from Store Settings. Edit here for this invoice only.</p>

                                    <div>
                                        <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">Company Name</label>
                                        <input
                                            type="text"
                                            value={sellerDetails.storeName}
                                            onChange={(e) => setSellerDetails({ ...sellerDetails, storeName: e.target.value })}
                                            className="w-full text-sm font-bold text-gray-900 border-none border-b border-gray-200 focus:border-blue-500 focus:ring-0 px-0 py-1"
                                            placeholder="Your Business Name"
                                        />
                                    </div>

                                    <div>
                                        <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">Tagline</label>
                                        <input
                                            type="text"
                                            value={sellerDetails.tagline}
                                            onChange={(e) => setSellerDetails({ ...sellerDetails, tagline: e.target.value })}
                                            className="w-full text-xs text-gray-600 border-none border-b border-gray-200 focus:border-blue-500 focus:ring-0 px-0 py-1"
                                            placeholder="e.g., Best Deals In Tech"
                                        />
                                    </div>

                                    <div>
                                        <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">Address</label>
                                        <textarea
                                            value={sellerDetails.address}
                                            onChange={(e) => setSellerDetails({ ...sellerDetails, address: e.target.value })}
                                            className="w-full text-xs text-gray-600 border border-gray-200 rounded p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                                            rows="2"
                                            placeholder="Shop address..."
                                        />
                                    </div>

                                    <div className="grid grid-cols-2 gap-3">
                                        <div>
                                            <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">Mobile</label>
                                            <input
                                                type="text"
                                                value={sellerDetails.phone}
                                                onChange={(e) => setSellerDetails({ ...sellerDetails, phone: e.target.value })}
                                                className="w-full text-xs text-gray-600 border border-gray-200 rounded px-2 py-1"
                                                placeholder="+91..."
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">Email</label>
                                            <input
                                                type="text"
                                                value={sellerDetails.email}
                                                onChange={(e) => setSellerDetails({ ...sellerDetails, email: e.target.value })}
                                                className="w-full text-xs text-gray-600 border border-gray-200 rounded px-2 py-1"
                                                placeholder="contact@..."
                                            />
                                        </div>
                                    </div>

                                    <div>
                                        <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">GSTIN</label>
                                        <input
                                            type="text"
                                            value={sellerDetails.gstin}
                                            onChange={(e) => setSellerDetails({ ...sellerDetails, gstin: e.target.value })}
                                            className="w-full text-xs text-gray-600 border border-gray-200 rounded px-2 py-1"
                                            placeholder="GSTIN"
                                        />
                                    </div>

                                    <div>
                                        <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">Bank Details</label>
                                        <textarea
                                            value={sellerDetails.bankDetails}
                                            onChange={(e) => setSellerDetails({ ...sellerDetails, bankDetails: e.target.value })}
                                            className="w-full text-xs text-gray-600 border border-gray-200 rounded p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                                            rows="2"
                                            placeholder="Bank Name, A/c No, IFSC..."
                                        />
                                    </div>

                                    <div>
                                        <label className="block text-[10px] uppercase font-bold text-gray-500 mb-1">UPI ID (for QR Code)</label>
                                        <input
                                            type="text"
                                            value={sellerDetails.upiId}
                                            onChange={(e) => setSellerDetails({ ...sellerDetails, upiId: e.target.value })}
                                            className="w-full text-xs text-gray-600 border border-gray-200 rounded px-2 py-1"
                                            placeholder="yourshop@upi"
                                        />
                                    </div>
                                </div>
                            )}
                        </div>

                    </div>

                    {/* CENTER CONTENT (6 cols) */}
                    <div className="lg:col-span-6 space-y-6">

                        {/* Add Products */}
                        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6">
                            <h3 className="font-semibold text-gray-800 mb-4">Add Products</h3>

                            <div className="flex flex-col md:flex-row gap-4 items-end">
                                <div className="flex-1 relative">
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Select from Inventory</label>
                                    <div className="relative">
                                        <input
                                            type="text"
                                            value={searchQuery}
                                            onChange={(e) => setSearchQuery(e.target.value)}
                                            placeholder="Choose a product..."
                                            className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                                        />
                                        {showProductSearch && searchQuery && filteredProducts.length > 0 && (
                                            <div className="absolute z-20 w-full mt-1 bg-white border border-gray-200 rounded-lg shadow-xl max-h-60 overflow-y-auto">
                                                {filteredProducts.map(p => (
                                                    <div
                                                        key={p._id}
                                                        onClick={() => addItem(p)}
                                                        className="p-3 hover:bg-gray-50 cursor-pointer border-b last:border-0 flex justify-between items-center"
                                                    >
                                                        <div>
                                                            <div className="font-medium text-gray-900">{p.name}</div>
                                                            <div className="text-xs text-gray-500">Stock: {p.stock} | SKU: {p.sku}</div>
                                                        </div>
                                                        <div className="font-semibold text-blue-600">{formatINR(p.sellingPrice)}</div>
                                                    </div>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                </div>
                                <div className="w-24">
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Qty</label>
                                    <input
                                        type="number"
                                        value={newItemConfig.qty}
                                        onChange={(e) => setNewItemConfig({ ...newItemConfig, qty: e.target.value })}
                                        className="w-full px-3 py-2.5 border border-gray-300 rounded-lg text-center"
                                        min="1"
                                    />
                                </div>
                                <button
                                    onClick={() => {
                                        if (filteredProducts.length > 0) addItem(filteredProducts[0]);
                                    }}
                                    className="px-6 py-2.5 bg-gray-900 text-white rounded-lg hover:bg-gray-800 flex items-center gap-2"
                                >
                                    <Plus className="w-4 h-4" /> Add
                                </button>
                            </div>

                            <div className="mt-4 pt-4 border-t border-gray-100">
                                <button
                                    onClick={() => addItem(null, true)}
                                    className="text-sm text-blue-600 hover:text-blue-800 flex items-center gap-2 font-medium px-3 py-2 border border-dashed border-blue-200 rounded-lg hover:bg-blue-50 transition-colors w-full md:w-auto justify-center"
                                >
                                    <Plus className="w-4 h-4" /> Add Custom Item (Service, Repair, etc.)
                                </button>
                            </div>

                            {/* Items Table */}
                            <div className="mt-6 overflow-x-auto rounded-lg border border-gray-200">
                                <table className="w-full text-sm text-left">
                                    <thead className="bg-gray-50 text-gray-600 font-medium">
                                        <tr>
                                            <th className="px-4 py-3 w-10">#</th>
                                            <th className="px-4 py-3 min-w-[200px]">ITEM NAME</th>
                                            <th className="px-4 py-3 w-20 text-center">QTY</th>
                                            <th className="px-4 py-3 w-40 text-right">RATE (Γé╣)</th>
                                            <th className="px-4 py-3 w-20 text-center">GST %</th>
                                            <th className="px-4 py-3 w-28 text-right">TAX AMT</th>
                                            <th className="px-4 py-3 w-32 text-right">TOTAL</th>
                                            <th className="px-4 py-3 w-10"></th>
                                        </tr>
                                    </thead>
                                    <tbody className="divide-y divide-gray-100">
                                        {items.length === 0 ? (
                                            <tr>
                                                <td colSpan="8" className="px-4 py-8 text-center text-gray-400">
                                                    No items added yet.
                                                </td>
                                            </tr>
                                        ) : (
                                            items.map((item, index) => (
                                                <tr key={index} className="hover:bg-gray-50 group">
                                                    <td className="px-4 py-3 text-gray-500">{index + 1}</td>
                                                    <td className="px-4 py-3">
                                                        <input
                                                            type="text"
                                                            value={item.name}
                                                            onChange={(e) => updateItem(index, 'name', e.target.value)}
                                                            className="w-full bg-transparent border-b border-transparent hover:border-gray-300 focus:border-blue-500 py-2 px-1 focus:ring-0 text-base font-semibold text-gray-900 placeholder-gray-400 transition-colors"
                                                            placeholder="Item Name"
                                                        />
                                                        {item.isCustom && <span className="text-[10px] text-blue-500 bg-blue-50 px-1.5 py-0.5 rounded ml-2">Custom</span>}

                                                        {/* Toggle for IMEI/Serial */}
                                                        {!item.showImei && (
                                                            <button
                                                                onClick={() => updateItem(index, 'showImei', true)}
                                                                className="ml-2 text-[10px] text-gray-400 hover:text-blue-500 transition-colors"
                                                            >
                                                                + Add IMEI/Serial
                                                            </button>
                                                        )}

                                                        {/* Conditional IMEI Inputs */}
                                                        {(item.showImei || (item.category && ['smartphone', 'keypad phone', 'mobile', 'phone', 'stub', 'cell', 'mi', 'vivo', 'oppo', 'samsung', 'iphone', 'apple', 'android', 'electronics'].some(c => item.category.toLowerCase().includes(c)))) && (
                                                            <div className="mt-2 grid grid-cols-2 gap-2 bg-orange-50 p-2 rounded-lg border border-orange-100">
                                                                <div>
                                                                    <input
                                                                        type="text"
                                                                        value={item.imei || ''}
                                                                        onChange={(e) => updateItem(index, 'imei', e.target.value)}
                                                                        className="w-full bg-white border border-gray-200 rounded px-2 py-1 text-xs text-center text-gray-700 placeholder-gray-400 focus:outline-none focus:border-blue-400 focus:ring-1 focus:ring-blue-400"
                                                                        placeholder="IMEI 1"
                                                                    />
                                                                </div>
                                                                <div>
                                                                    <input
                                                                        type="text"
                                                                        value={item.imei2 || ''}
                                                                        onChange={(e) => updateItem(index, 'imei2', e.target.value)}
                                                                        className="w-full bg-white border border-gray-200 rounded px-2 py-1 text-xs text-center text-gray-700 placeholder-gray-400 focus:outline-none focus:border-blue-400 focus:ring-1 focus:ring-blue-400"
                                                                        placeholder="IMEI 2"
                                                                    />
                                                                </div>
                                                            </div>
                                                        )}

                                                        {/* Conditional Serial Number Input */}
                                                        {(item.showImei || (item.category && ['watch', 'laptop', 'buds', 'audio', 'speaker', 'serial', 'macbook', 'ipad', 'tablet', 'electronics'].some(c => item.category.toLowerCase().includes(c)))) && (
                                                            <div className="mt-2">
                                                                <input
                                                                    type="text"
                                                                    value={item.serialNumber || ''}
                                                                    onChange={(e) => updateItem(index, 'serialNumber', e.target.value)}
                                                                    className="w-full bg-blue-50 border border-blue-100 rounded px-2 py-1 text-xs text-gray-700 placeholder-gray-400 focus:outline-none focus:border-blue-400"
                                                                    placeholder="Serial Number (Optional)"
                                                                />
                                                            </div>
                                                        )}
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <input
                                                            type="number"
                                                            value={item.quantity}
                                                            onChange={(e) => updateItem(index, 'quantity', e.target.value)}
                                                            className="w-full bg-white border border-gray-200 rounded px-2 py-1 text-center text-sm focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                                                        />
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <div className="relative">
                                                            <input
                                                                type="number"
                                                                value={item.pricePerUnit}
                                                                onChange={(e) => updateItem(index, 'pricePerUnit', e.target.value)}
                                                                className="w-full bg-white border border-gray-200 rounded pl-2 pr-1 py-2 text-right text-base font-bold text-gray-900 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                                                            />
                                                        </div>
                                                    </td>
                                                    <td className="px-4 py-3">
                                                        <select
                                                            value={item.gstPercent}
                                                            onChange={(e) => updateItem(index, 'gstPercent', e.target.value)}
                                                            className="w-full bg-white border border-gray-200 rounded px-1 py-1 text-center text-sm focus:border-blue-500 focus:ring-1 focus:ring-blue-500 appearance-none"
                                                        >
                                                            <option value="0">0%</option>
                                                            <option value="5">5%</option>
                                                            <option value="12">12%</option>
                                                            <option value="18">18%</option>
                                                            <option value="28">28%</option>
                                                        </select>
                                                    </td>
                                                    <td className="px-4 py-3 text-right text-gray-600">
                                                        {formatINR(item.gstAmount)}
                                                    </td>
                                                    <td className="px-4 py-3 text-right font-semibold text-gray-900">
                                                        {formatINR(item.totalAmount)}
                                                    </td>
                                                    <td className="px-4 py-3 text-center">
                                                        <button
                                                            onClick={() => removeItem(index)}
                                                            className="text-red-400 hover:text-red-600 opacity-0 group-hover:opacity-100 transition-opacity"
                                                        >
                                                            <Trash2 className="w-4 h-4" />
                                                        </button>
                                                    </td>
                                                </tr>
                                            ))
                                        )}
                                    </tbody>
                                </table>
                            </div>

                            {/* Totals Summary */}
                            {items.length > 0 && (
                                <div className="mt-4 flex justify-end">
                                    <div className="w-64 space-y-2 text-sm">
                                        <div className="flex justify-between text-gray-600">
                                            <span>Taxable Value:</span>
                                            <span>{formatINR(summary.totalTaxable)}</span>
                                        </div>
                                        <div className="flex justify-between text-gray-600">
                                            <span>Total GST:</span>
                                            <span>{formatINR(summary.totalGST)}</span>
                                        </div>
                                        <div className="flex justify-between text-blue-600 font-bold text-lg pt-2 border-t border-gray-100">
                                            <span>Grand Total:</span>
                                            <span>{formatINR(summary.grandTotal)}</span>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Payment Breakdown (Inline) */}
                        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6">
                            <h3 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
                                <div className="p-1 bg-green-100 rounded text-green-600">
                                    <div className="w-3 h-3 rounded-full border-2 border-current" />
                                </div>
                                Payment Breakdown
                            </h3>

                            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Cash</label>
                                    <input
                                        type="number"
                                        value={payment.cash || ''}
                                        onChange={(e) => setPayment({ ...payment, cash: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm bg-gray-50 focus:bg-white transition-colors"
                                        placeholder="0.00"
                                    />
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">UPI (GPay/PhonePe)</label>
                                    <input
                                        type="number"
                                        value={payment.upi || ''}
                                        onChange={(e) => setPayment({ ...payment, upi: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm bg-gray-50 focus:bg-white transition-colors"
                                        placeholder="0.00"
                                    />
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Card</label>
                                    <input
                                        type="number"
                                        value={payment.card || ''}
                                        onChange={(e) => setPayment({ ...payment, card: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm bg-gray-50 focus:bg-white transition-colors"
                                        placeholder="0.00"
                                    />
                                </div>
                                <div>
                                    <label className="block text-xs font-medium text-gray-500 mb-1">Bank Transfer</label>
                                    <input
                                        type="number"
                                        value={payment.bank || ''}
                                        onChange={(e) => setPayment({ ...payment, bank: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm bg-gray-50 focus:bg-white transition-colors"
                                        placeholder="0.00"
                                    />
                                </div>
                            </div>

                            <div className="flex flex-col md:flex-row justify-between items-center p-4 bg-gray-50 rounded-xl border border-gray-200">
                                <div className="space-y-1 mb-4 md:mb-0">
                                    <div className="text-sm text-gray-600">
                                        Total Paid: <span className="font-semibold text-green-600">{formatINR(totalPaid)}</span>
                                    </div>
                                    <div className="text-lg font-bold text-gray-900 flex items-center gap-2">
                                        Balance Due (Udhaar):
                                        <span className={`text-xl ${balanceDue > 0 ? 'text-orange-500' : 'text-gray-400'}`}>
                                            {formatINR(balanceDue > 0 ? balanceDue : 0)}
                                        </span>
                                    </div>
                                    <div className="flex items-center gap-2 mt-1">
                                        <span className="text-xs font-medium text-gray-500">Status:</span>
                                        {isFullyPaid ? (
                                            <span className="px-2 py-1 bg-green-100 text-green-700 text-xs font-bold rounded-full">Paid</span>
                                        ) : (
                                            <span className="px-2 py-1 bg-red-100 text-red-700 text-xs font-bold rounded-full flex items-center gap-1">
                                                Credit / Udhaar
                                                <span className="font-normal opacity-75 text-[10px]">(Added to customer ledger)</span>
                                            </span>
                                        )}
                                    </div>
                                </div>

                                <div className="flex gap-3">
                                    <button
                                        onClick={() => navigate('/dashboard')}
                                        className="px-5 py-2.5 border border-gray-300 rounded-lg text-gray-700 font-medium hover:bg-white transition-colors"
                                    >
                                        Cancel
                                    </button>
                                    <button
                                        onClick={handleSubmit}
                                        disabled={loading}
                                        className="px-8 py-2.5 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700 shadow-lg shadow-blue-200 transition-all flex items-center gap-2"
                                    >
                                        {loading ? 'Saving...' : 'Save TAX INVOICE'}
                                        {!loading && <Save className="w-4 h-4" />}
                                    </button>
                                </div>
                            </div>
                        </div>

                    </div>

                    {/* RIGHT PREVIEW PANEL (3 cols) */}
                    <div className="lg:col-span-3">
                        <div className="sticky top-6">
                            <h3 className="text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
                                <Printer className="w-4 h-4" />
                                Live Invoice Preview
                            </h3>

                            {/* Invoice Preview Card */}
                            <div className="bg-white shadow-xl rounded-lg overflow-hidden border border-gray-200 text-xs">
                                {/* Header - Simplified Design */}
                                {/* Header - Modern Clean Design */}
                                {false ? (
                                    <div className="w-full">
                                        <img
                                            src={settings.letterhead.startsWith('http') ? settings.letterhead : `http://localhost:5000${settings.letterhead}`}
                                            alt="Letterhead"
                                            className="w-full h-auto object-contain"
                                        />
                                    </div>
                                ) : (
                                    <div className="p-4 bg-white">
                                        <div className="flex justify-between items-start mb-2">
                                            {/* Left: Branding */}
                                            <div className="flex items-center gap-3">
                                                {settings?.logo && (
                                                    <img
                                                        src={settings.logo.startsWith('http') ? settings.logo : `http://localhost:5000${settings.logo}`}
                                                        className="h-10 w-auto object-contain"
                                                        alt="Logo"
                                                    />
                                                )}
                                                <div>
                                                    <h1 className="text-sm font-bold uppercase tracking-wider text-gray-800 leading-tight">
                                                        {sellerDetails.storeName || ''}
                                                    </h1>
                                                    <p className="text-[9px] text-gray-500 tracking-[0.2em]">
                                                        {sellerDetails.tagline || ''}
                                                    </p>
                                                </div>
                                            </div>

                                            {/* Right: Contact & Invoice Meta */}
                                            <div className="text-right">
                                                <div className="mb-2">
                                                    <div className="font-bold text-[10px] uppercase tracking-wide text-gray-900">{invoiceSettings.title}</div>
                                                    <div className="text-[9px] text-gray-500">{invoiceSettings.invoiceNo}</div>
                                                    <div className="text-[9px] text-gray-500">{invoiceSettings.date}</div>
                                                </div>

                                                <div className="text-[9px] text-gray-600 space-y-0.5 font-medium">
                                                    {sellerDetails.address ? (
                                                        <>
                                                            <p>{sellerDetails.address.split(',')[0]}</p>
                                                            <p>{sellerDetails.address.split(',')[1]}</p>
                                                        </>
                                                    ) : null}
                                                    {sellerDetails.phone && (
                                                        <div className="flex items-center justify-end gap-1.5 mt-1">
                                                            <span>{sellerDetails.phone}</span>
                                                            <span className="text-[8px]" style={{ color: settings?.brandColor || '#EF4444' }}>≡ƒô₧</span>
                                                        </div>
                                                    )}
                                                    {sellerDetails.email && (
                                                        <div className="flex items-center justify-end gap-1.5">
                                                            <span>{sellerDetails.email}</span>
                                                            <span className="text-[8px]" style={{ color: settings?.brandColor || '#EF4444' }}>Γ£ë∩╕Å</span>
                                                        </div>
                                                    )}
                                                    {sellerDetails.website && (
                                                        <div className="flex items-center justify-end gap-1.5">
                                                            <span>{sellerDetails.website}</span>
                                                            <span className="text-[8px]" style={{ color: settings?.brandColor || '#EF4444' }}>≡ƒîÉ</span>
                                                        </div>
                                                    )}
                                                    {sellerDetails.gstin && (
                                                        <div className="flex items-center justify-end gap-1.5 mt-1 font-semibold">
                                                            <span>GSTIN: {sellerDetails.gstin}</span>
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        </div>

                                        {/* Custom Divider Line */}
                                        <div className="flex items-center mt-2 mb-1">
                                            <div className="h-1.5 rounded-l-full w-1/3" style={{ backgroundColor: settings?.brandColor || '#EF4444' }}></div>
                                            <div className="h-0.5 bg-gray-800 w-2/3 rounded-r-full"></div>
                                        </div>
                                    </div>
                                )}

                                {/* From/To Section */}
                                <div className="p-3 grid grid-cols-2 gap-3 border-b">
                                    <div>
                                        <div className="text-[8px] font-semibold text-gray-600 mb-0.5">From:</div>
                                        <p className="text-[10px] font-semibold">{sellerDetails.storeName || ''}</p>
                                        <p className="text-[9px] text-gray-700">{sellerDetails.address || ''}</p>
                                        <p className="text-[9px] text-gray-700">{sellerDetails.phone}</p>
                                    </div>
                                    <div>
                                        <div className="text-[8px] font-semibold text-gray-600 mb-0.5">To:</div>
                                        <p className="text-[10px] font-semibold">{customerInfo.name || ''}</p>
                                        <p className="text-[9px] text-gray-700">{customerInfo.phone || ''}</p>
                                    </div>
                                </div>

                                {/* Items Table */}
                                <div className="p-4">
                                    <table className="w-full text-[9px]">
                                        <thead>
                                            <tr className="text-white" style={{ backgroundColor: settings?.brandColor || '#1e3a8a' }}>
                                                <th className="py-1.5 px-2 text-left">#</th>
                                                <th className="py-1.5 px-2 text-left">Item</th>
                                                <th className="py-1.5 px-2 text-center">Qty</th>
                                                <th className="py-1.5 px-2 text-right">Total</th>
                                            </tr>
                                        </thead>
                                        <tbody className="divide-y divide-gray-100">
                                            {items.length === 0 ? (
                                                <tr>
                                                    <td colSpan="4" className="py-4 text-center text-gray-400 text-[10px]">
                                                        No items added
                                                    </td>
                                                </tr>
                                            ) : (
                                                items.map((item, index) => (
                                                    <tr key={index} className="hover:bg-gray-50 border-b border-gray-50 last:border-0">
                                                        <td className="py-2 px-2 text-gray-500 align-top">{index + 1}</td>
                                                        <td className="py-2 px-2 align-top">
                                                            <div className="font-medium text-gray-900">{item.name}</div>
