import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../utils/api';
import { useSettings } from '../context/SettingsContext';
import { formatINR } from '../utils/currency';
import { formatDate } from '../utils/date';
import { Printer, ArrowLeft, Download, Share2, Mail } from 'lucide-react';
import toast from 'react-hot-toast';
import { QRCodeSVG } from 'qrcode.react';

const InvoiceView = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { settings } = useSettings();
    const [invoice, setInvoice] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        loadInvoice();
    }, [id]);

    const loadInvoice = async () => {
        try {
            const { data } = await api.get(`/invoices/${id}`);
            setInvoice(data);
            setLoading(false);
        } catch (error) {
            console.error('Failed to load invoice:', error);
            toast.error('Failed to load invoice');
            navigate('/invoices');
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gray-50">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    if (!invoice) return null;

    // Use invoice-specific seller details if available (Snapshot), otherwise fallback to global settings
    const seller = invoice.sellerDetails || {
        storeName: settings?.storeName || settings?.shopName,
        tagline: settings?.tagline,
        address: settings?.address,
        phone: settings?.phone,
        email: settings?.email,
        gstin: settings?.gstin || settings?.gstNumber,
        website: settings?.website,
        bankDetails: (settings?.bankName && settings?.accountNumber)
            ? `${settings.bankName}, A/c: ${settings.accountNumber}, IFSC: ${settings.ifscCode}`
            : '',
        upiId: settings?.upi || settings?.upiId
    };

    return (
        <div className="min-h-screen bg-gray-100 p-6 print:p-0 print:bg-white">
            {/* Toolbar - Hidden in Print */}
            <div className="max-w-4xl mx-auto mb-6 flex items-center justify-between print:hidden">
                <button
                    onClick={() => navigate('/invoices')}
                    className="flex items-center gap-2 text-gray-600 hover:text-gray-900 transition-colors"
                >
                    <ArrowLeft className="w-5 h-5" />
                    Back to History
                </button>
                <div className="flex items-center gap-3">
                    <button
                        onClick={() => window.print()}
                        className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors shadow-sm"
                    >
                        <Printer className="w-4 h-4" />
                        Print Invoice
                    </button>
                </div>
            </div>

            {/* Invoice Paper */}
            <div className="max-w-4xl mx-auto bg-white shadow-xl rounded-xl overflow-hidden print:shadow-none print:rounded-none print:max-w-none">
                {/* Header - Simplified Design */}
                <div className="p-4 flex justify-between items-center" style={{ backgroundColor: settings?.brandColor || '#1e3a8a' }}>
                    {/* Left: Company Name */}
                    <h1 className="text-xl font-bold text-white">
                        {seller.storeName || seller.shopName || ''}
                    </h1>

                    {/* Right: TAX INVOICE Details */}
                    <div className="text-right text-white">
                        <div className="font-bold text-sm uppercase tracking-wider">TAX INVOICE</div>
                        <div className="text-xs mt-0.5">INV-{invoice.invoiceNo}</div>
                        <div className="text-xs">{formatDate(invoice.invoiceDate || invoice.createdAt)}</div>
                    </div>
                </div>

                {/* Customer Info - Two Column Layout */}
                <div className="p-4 border-b grid grid-cols-2 gap-6">
                    {/* Left: From (Shop Details) */}
                    <div>
                        <div className="text-xs font-semibold text-gray-600 mb-1">From:</div>
                        <div className="text-sm text-gray-800 space-y-0.5">
                            <p className="font-semibold">{seller.storeName || seller.shopName}</p>
                            <p>{seller.address}</p>
                            <p>{seller.phone}</p>
                            {seller.email && <p>{seller.email}</p>}
                            {seller.website && <p>{seller.website}</p>}
                            {(seller.gstin || seller.gstNumber) && (
                                <p className="text-xs text-gray-600 font-semibold">GSTIN: {seller.gstin || seller.gstNumber}</p>
                            )}
                        </div>
                    </div>

                    {/* Right: To (Customer Details) */}
                    <div>
                        <div className="text-xs font-semibold text-gray-600 mb-1">To:</div>
                        <div className="text-sm text-gray-800 space-y-0.5">
                            <p className="font-semibold">{invoice.customer?.name || invoice.customerName || 'Walk-in Customer'}</p>
                            <p>{invoice.customerPhone}</p>
                            {invoice.customer?.address && <p className="max-w-xs">{invoice.customer.address}</p>}
                            {invoice.customer?.gstin && (
                                <p className="text-xs text-gray-600">GSTIN: {invoice.customer.gstin}</p>
                            )}
                        </div>
                    </div>
                </div>

                {/* Items Table */}
                <div className="p-6">
                    <table className="w-full text-sm">
                        <thead>
                            <tr className="text-left text-white" style={{ backgroundColor: settings?.brandColor || '#1e3a8a' }}>
                                <th className="py-3 px-3 font-semibold w-12">#</th>
                                <th className="py-3 px-3 font-semibold">Item</th>
                                <th className="py-3 px-3 font-semibold text-center w-20">Qty</th>
                                <th className="py-3 px-3 font-semibold text-right w-32">Rate</th>
                                <th className="py-3 px-3 font-semibold text-center w-24">GST %</th>
                                <th className="py-3 px-3 font-semibold text-right w-32">Total</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-100">
                            {invoice.items.map((item, index) => (
                                <tr key={index} className="group hover:bg-gray-50">
                                    <td className="py-4 px-3 text-gray-500 align-top">{index + 1}</td>
                                    <td className="py-4 px-3 align-top">
                                        <p className="font-medium text-gray-900">{item.productName || item.itemName}</p>

                                        {/* IMEI/Serial Display */}
                                        {(item.imei || item.imei2 || item.serialNumber) && (
                                            <div className="text-[10px] text-gray-500 mt-1 font-mono space-y-0.5">
                                                {item.imei && <p>IMEI 1: <span className="text-gray-700 font-semibold">{item.imei}</span></p>}
                                                {item.imei2 && <p>IMEI 2: <span className="text-gray-700 font-semibold">{item.imei2}</span></p>}
                                                {item.serialNumber && <p>Serial: <span className="text-gray-700 font-semibold">{item.serialNumber}</span></p>}
                                            </div>
                                        )}

                                        {item.isCustom && (
                                            <span className="inline-block mt-1 text-[10px] bg-blue-50 text-blue-600 px-1.5 py-0.5 rounded">
                                                Custom
                                            </span>
                                        )}
                                    </td>
                                    <td className="py-4 px-3 text-center text-gray-700 align-top">{item.quantity}</td>
                                    <td className="py-4 px-3 text-right text-gray-700 align-top rupee">{formatINR(item.pricePerUnit)}</td>
                                    <td className="py-4 px-3 text-center text-gray-500 align-top">
                                        {item.gstPercent > 0 ? `${item.gstPercent}%` : '-'}
                                    </td>
                                    <td className="py-4 px-3 text-right font-medium text-gray-900 align-top rupee">
                                        {formatINR(item.totalAmount)}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Totals Section - Right Aligned */}
                <div className="p-4 flex justify-end border-t">
                    <div className="w-72 space-y-1">
                        <div className="flex justify-between text-sm">
                            <span>Subtotal:</span>
                            <span className="rupee">{formatINR(invoice.totalTaxable)}</span>
                        </div>
                        <div className="flex justify-between text-sm">
                            <span>Total GST:</span>
                            <span className="rupee">{formatINR(invoice.totalGST)}</span>
                        </div>
                        {/* GRAND TOTAL with Blue Badge */}
                        <div className="flex justify-between font-bold text-white p-2 rounded mt-2"
                            style={{ backgroundColor: settings?.brandColor || '#1e3a8a' }}>
                            <span className="uppercase tracking-wide">GRAND TOTAL:</span>
                            <span className="rupee text-lg">{formatINR(invoice.grandTotal)}</span>
                        </div>
                    </div>
                </div>

                {/* Bottom Section - Footer */}
                <div className="p-4 border-t">
                    <div className="grid grid-cols-2 gap-6">
                        {/* Left: QR Code, Notes, Bank Details */}
                        <div className="space-y-3">
                            {/* QR Code */}
                            {seller.upiId && (
                                <div>
                                    <QRCodeSVG
                                        value={`upi://pay?pa=${seller.upiId}&pn=${encodeURIComponent(seller.storeName || seller.shopName || 'Store')}`}
                                        size={80}
                                        level="M"
                                        includeMargin={false}
                                    />
                                    <div className="text-xs text-gray-500 mt-1">
                                        {settings?.digitalSignature ? 'Scan to Pay' : 'ghfghfghfghf'}
                                    </div>
                                </div>
                            )}

                            {/* Notes */}
                            <div className="text-xs text-gray-600 space-y-0.5">
                                <p>ΓÇó Goods once sold will not be taken back</p>
                                <p>ΓÇó Payment due within 30 days</p>
                            </div>

                            {/* Bank Details */}
                            {(settings?.bankName || settings?.accountNumber || settings?.ifscCode) && (
                                <div>
                                    <div className="font-semibold text-xs mb-1">Bank Details:</div>
                                    <div className="text-xs text-gray-700 space-y-0.5">
                                        {settings?.bankName && <p>{settings.bankName}</p>}
                                        {settings?.accountNumber && <p>{settings.accountNumber}</p>}
                                        {settings?.ifscCode && <p>{settings.ifscCode}</p>}
                                        {settings?.bankBranch && <p>{settings.bankBranch}</p>}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Right: Authorized Sign */}
                        <div className="flex flex-col items-end justify-end">
                            {settings?.digitalSignature && (
                                <img
                                    src={settings.digitalSignature.startsWith('http') ? settings.digitalSignature : `http://localhost:5000${settings.digitalSignature}`}
                                    alt="Signature"
                                    className="w-32 h-16 object-contain mb-2"
                                />
                            )}
                            <div className="text-sm font-medium">Authorized Sign</div>
                        </div>
                    </div>
                </div>

            </div>

            {/* Print Footer */}
            <div className="p-4 border-t border-gray-100 text-center text-xs text-gray-400 print:block hidden">
                <p>Computer Generated Invoice ΓÇó {seller.storeName || seller.shopName}</p>
            </div>
        </div>
    );

};

export default InvoiceView;

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import Layout from '../components/Layout/Layout';
import api from '../utils/api';
import { useSettings } from '../context/SettingsContext';
import { ArrowLeft, Save, Printer, Type, Layout as LayoutIcon } from 'lucide-react';
import toast from 'react-hot-toast';

const LetterheadCreator = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const location = useLocation();
    const isViewMode = location.pathname.includes('/view/');
    const { settings } = useSettings();
    const [loading, setLoading] = useState(false);

    // Document Content
    const [recipient, setRecipient] = useState({ name: '', address: '', email: '' });
    const [subject, setSubject] = useState('');
    const [content, setContent] = useState('');

    // Copy Global Config to Local State (for potential overrides, though we stick to Global for now)
    const [config, setConfig] = useState(null);

    useEffect(() => {
        if (settings?.letterheadConfig && !id) {
            setConfig(settings.letterheadConfig);
        }
    }, [settings, id]);

    useEffect(() => {
        if (id) {
            loadLetterhead();
        }
    }, [id]);

    const loadLetterhead = async () => {
        try {
            setLoading(true);
            const { data } = await api.get(`/letterheads/${id}`);
            setRecipient(data.recipient || { name: '', address: '', email: '' });
            setSubject(data.subject);
            setContent(data.content);
            if (data.configSnapshot) {
                setConfig(data.configSnapshot);
            } else if (settings?.letterheadConfig) {
                setConfig(settings.letterheadConfig);
            }
        } catch (error) {
            console.error(error);
            toast.error('Failed to load letterhead');
            navigate('/letterheads');
        } finally {
            setLoading(false);
        }
    };

    const handleSave = async (status = 'Draft') => {
        if (!subject || !content) {
            toast.error('Subject and Content are required');
            return;
        }
        try {
            setLoading(true);
            const data = {
                recipient,
                subject,
                content,
                status
            };

            if (id) {
                await api.put(`/letterheads/${id}`, data);
                toast.success('Letterhead updated');
            } else {
                await api.post('/letterheads', data);
                toast.success('Letterhead created');
            }
            navigate('/letterheads');
        } catch (error) {
            console.error(error);
            toast.error('Failed to save letterhead');
        } finally {
            setLoading(false);
        }
    };

    const handlePrint = () => {
        window.print();
    };

    if (!config) return <Layout><div className="p-6">Loading config...</div></Layout>;

    return (
        <Layout>
            <div className="p-6 h-[calc(100vh-64px)] overflow-hidden flex flex-col no-print">
                {/* Header */}
                <div className="flex justify-between items-center mb-6">
                    <div className="flex items-center space-x-4">
                        <button onClick={() => navigate('/letterheads')} className="p-2 hover:bg-gray-100 rounded-full">
                            <ArrowLeft className="w-5 h-5 text-gray-600" />
                        </button>
                        <h1 className="text-2xl font-bold text-gray-900">Create Letter</h1>
                    </div>
                    <div className="flex space-x-3">
                        <button onClick={handlePrint} className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">
                            <Printer className="w-4 h-4" />
                            <span>Print / PDF</span>
                        </button>
                        {!isViewMode && (
                            <button onClick={() => handleSave('Final')} disabled={loading} className="flex items-center space-x-2 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50">
                                <Save className="w-4 h-4" />
                                <span>{loading ? 'Saving...' : id ? 'Update' : 'Save Document'}</span>
                            </button>
                        )}
                    </div>
                </div>

                <div className="flex-1 flex gap-6 overflow-hidden">
                    {/* Editor Panel */}
                    <div className="w-1/3 flex flex-col bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
                        <div className="p-4 bg-gray-50 border-b flex items-center justify-between">
                            <h3 className="font-semibold text-gray-700 flex items-center">
                                <Type className="w-4 h-4 mr-2" /> Content Editor
                            </h3>
                        </div>
                        <div className="flex-1 overflow-y-auto p-4 space-y-4">
                            {/* Recipient */}
                            <div className="space-y-3 p-3 bg-gray-50 rounded-lg">
                                <span className="text-xs font-bold text-gray-500 uppercase">Recipient Details</span>
                                <input
                                    type="text"
                                    placeholder="Recipient Name"
                                    value={recipient.name}
                                    onChange={(e) => setRecipient({ ...recipient, name: e.target.value })}
                                    className="w-full px-3 py-2 border rounded text-sm"
                                />
                                <textarea
                                    placeholder="Address / Designation"
                                    value={recipient.address}
                                    onChange={(e) => setRecipient({ ...recipient, address: e.target.value })}
                                    className="w-full px-3 py-2 border rounded text-sm h-16 resize-none"
                                />
                            </div>

                            {/* Subject */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">Subject</label>
                                <input
                                    type="text"
                                    placeholder="Subject of the letter..."
                                    value={subject}
                                    onChange={(e) => setSubject(e.target.value)}
                                    className="w-full px-3 py-2 border rounded-lg font-medium"
                                />
                            </div>

                            {/* Body */}
                            <div className="flex-1 flex flex-col">
                                <label className="block text-sm font-medium text-gray-700 mb-1">Body Content</label>
                                <textarea
                                    placeholder="Type your letter content here..."
                                    value={content}
                                    onChange={(e) => setContent(e.target.value)}
                                    className="w-full flex-1 p-3 border rounded-lg text-sm leading-relaxed resize-none min-h-[300px]"
                                    style={{ fontFamily: config.fontFamily }}
                                />
                                <p className="text-xs text-gray-400 mt-2">
                                    HTML or Markdown not supported in this version. Plain text only.
                                </p>
                            </div>
                        </div>
                    </div>

                    {/* Preview Panel (A4) */}
                    <div className="flex-1 bg-gray-200 rounded-xl overflow-auto flex justify-center p-8">
                        <div className="printable-content bg-white shadow-2xl relative transition-all duration-300"
                            style={{
                                width: '210mm',
                                minHeight: '297mm',
                                paddingTop: `${config.marginTop}mm`,
                                paddingBottom: `${config.marginBottom}mm`,
                                paddingLeft: `${config.marginLeft}mm`,
                                paddingRight: `${config.marginRight}mm`,
                                fontFamily: config.fontFamily || 'Inter',
                                position: 'relative'
                            }}
                        >
                            {/* Watermark */}
                            {config.watermarkText && (
                                <div className="absolute inset-0 flex items-center justify-center z-0 pointer-events-none overflow-hidden">
                                    <div
                                        className="font-bold text-gray-900 transform -rotate-45 whitespace-nowrap select-none"
                                        style={{
                                            opacity: config.watermarkOpacity,
                                            fontSize: `${config.watermarkSize || 100}px`
                                        }}
                                    >
                                        {config.watermarkText}
                                    </div>
                                </div>
                            )}

                            {/* Header - Liceria Style */}
                            <div className="relative z-10 flex justify-between items-start mb-4">
                                {/* Branding (Left) */}
                                <div className="flex flex-col items-start">
                                    <div className="flex items-center gap-3 mb-2">
                                        {settings?.logo && (
                                            <img src={settings.logo} className="h-16 w-auto object-contain" alt="Logo" />
                                        )}
                                        <div>
                                            <h1 className="text-3xl font-bold text-gray-800 uppercase tracking-wide leading-none">{settings.shopName || ''}</h1>
                                            <p className="text-sm text-gray-500 tracking-[0.2em]">{settings.tagline || ''}</p>
                                        </div>
                                    </div>
                                </div>

                                {/* Contact Info (Right) */}
                                <div className="text-right text-xs text-gray-600 leading-tight">
                                    <p className="font-semibold text-gray-800">{settings.address?.split('\n')[0]}</p>
                                    <p>{settings.address?.split('\n')[1]}</p>
                                    {settings.phone && (
                                        <p className="mt-2 flex items-center justify-end gap-2">
                                            {settings.phone} <span className="text-[10px] bg-red-500 text-white rounded-full p-1 w-5 h-5 flex items-center justify-center">≡ƒô₧</span>
                                        </p>
                                    )}
                                    {settings.email && (
                                        <p className="flex items-center justify-end gap-2 mt-1">
                                            {settings.email} <span className="text-[10px] bg-red-500 text-white rounded-full p-1 w-5 h-5 flex items-center justify-center">Γ£ë∩╕Å</span>
                                        </p>
                                    )}
                                    {settings.website && (
                                        <p className="flex items-center justify-end gap-2 mt-1">
                                            {settings.website} <span className="text-[10px] bg-red-500 text-white rounded-full p-1 w-5 h-5 flex items-center justify-center">≡ƒîÉ</span>
                                        </p>
                                    )}
                                    {settings.gstNumber && (
                                        <p className="flex items-center justify-end gap-2 mt-1 font-semibold">
                                            GSTIN: {settings.gstNumber}
                                        </p>
                                    )}
                                </div>
                            </div>

                            {/* Graphical Separator - Red/Black Line */}
                            <div className="relative z-10 w-full flex h-2 mb-10">
                                <div className="w-1/3" style={{ backgroundColor: settings.brandColor || '#EF4444' }}></div>
                                <div className="flex-1 bg-gray-900"></div>
                            </div>

                            {/* Letter Content */}
                            <div className="relative z-10 text-gray-900 leading-relaxed whitespace-pre-wrap">
                                {/* Date */}
                                <div className="mb-8 text-right font-medium">
                                    {new Date().toLocaleDateString('en-IN', { year: 'numeric', month: 'long', day: 'numeric' })}
                                </div>

                                {/* Recipient Block */}
                                {(recipient.name || recipient.address) && (
                                    <div className="mb-12">
                                        <p className="font-bold text-lg">{recipient.name}</p>
                                        <p className="whitespace-pre-line text-gray-600">{recipient.address}</p>
                                    </div>
                                )}

                                {/* Subject */}
                                {subject && (
                                    <div className="mb-8 font-bold text-lg underline decoration-2 underline-offset-4 decoration-gray-400">
                                        Subject: {subject}
                                    </div>
                                )}

                                {/* Body */}
                                <div className="min-h-[300px] text-justify text-base">
                                    {content || <span className="text-gray-300 italic">Start typing content...</span>}
                                </div>

                                {/* Signature Block */}
                                <div className="mt-20 text-right">
                                    <p className="font-bold text-lg">{settings.shopName}</p>
                                    <div className="h-24"></div>
                                    <p className="text-sm text-gray-600 border-t inline-block pt-2 px-8 border-gray-400">Authorized Signatory</p>
                                </div>
                            </div>

                            {/* Footer - 4 Color Blocks */}
                            <div className="absolute bottom-0 left-0 right-0 h-12 flex">
                                <div className="flex-1" style={{ backgroundColor: settings.brandColor || '#EF4444' }}></div>
                                <div className="flex-1" style={{ backgroundColor: settings.brandColor || '#EF4444', filter: 'brightness(0.9)' }}></div>
                                <div className="flex-1" style={{ backgroundColor: settings.brandColor || '#EF4444', filter: 'brightness(0.8)' }}></div>
                                <div className="flex-1" style={{ backgroundColor: settings.brandColor || '#EF4444', filter: 'brightness(0.7)' }}></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Print Styles */}
            <style>{`
                @media print {
                    @page { size: A4; margin: 0; }
                    body { margin: 0; background: white; }
                    .no-print { display: none !important; }
                    .printable-content {
                        box-shadow: none !important;
                        margin: 0 !important;
                        width: 210mm !important;
                        height: 297mm !important;
                        position: absolute !important;
                        top: 0 !important;
                        left: 0 !important;
                        overflow: hidden !important;
                    }
                }
            `}</style>
        </Layout>
    );
};

export default LetterheadCreator;

