import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout/Layout';
import api from '../utils/api';
import { Plus, Search, FileText, Trash2, Printer, Edit } from 'lucide-react';
import toast from 'react-hot-toast';
import { formatDate } from '../utils/date';

const LetterheadList = () => {
    const navigate = useNavigate();
    const [letterheads, setLetterheads] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        loadLetterheads();
    }, [searchTerm]);

    const loadLetterheads = async () => {
        try {
            setLoading(true);
            const { data } = await api.get(`/letterheads?search=${searchTerm}`);
            setLetterheads(data);
            setLoading(false);
        } catch (error) {
            console.error(error);
            toast.error('Failed to load letterheads');
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Delete this letterhead document? This cannot be undone.')) return;
        try {
            await api.delete(`/letterheads/${id}`);
            toast.success('Deleted successfully');
            loadLetterheads();
        } catch (error) {
            toast.error('Failed to delete');
        }
    };

    return (
        <Layout>
            <div className="p-6">
                <div className="flex justify-between items-center mb-6">
                    <div>
                        <h1 className="text-2xl font-bold text-gray-900">Letterheads</h1>
                        <p className="text-gray-600 mt-1">Manage your official correspondence</p>
                    </div>
                    <button
                        onClick={() => navigate('/letterheads/create')}
                        className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
                    >
                        <Plus className="w-5 h-5" />
                        <span>Create New</span>
                    </button>
                </div>

                {/* Search */}
                <div className="relative mb-6">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                    <input
                        type="text"
                        placeholder="Search by subject, recipient..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>

                {/* Table */}
                <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
                    {loading ? (
                        <div className="text-center py-12">Loading...</div>
                    ) : letterheads.length === 0 ? (
                        <div className="text-center py-12 text-gray-500">
                            <FileText className="w-12 h-12 mx-auto mb-3 opacity-30" />
                            <p>No letterheads found.</p>
                        </div>
                    ) : (
                        <table className="w-full">
                            <thead className="bg-gray-50 border-b border-gray-200">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-500 uppercase">Number</th>
                                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-500 uppercase">Date</th>
                                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-500 uppercase">Recipient</th>
                                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-500 uppercase">Subject</th>
                                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-500 uppercase">Actions</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y divide-gray-200">
                                {letterheads.map((doc) => (
                                    <tr key={doc._id} className="hover:bg-gray-50 transition">
                                        <td className="px-6 py-4 font-medium text-gray-900">{doc.letterheadNo}</td>
                                        <td className="px-6 py-4 text-gray-500">{formatDate(doc.date)}</td>
                                        <td className="px-6 py-4 text-gray-900 font-medium">
                                            {doc.recipient?.name || 'Unknown'}
                                        </td>
                                        <td className="px-6 py-4 text-gray-600 truncate max-w-xs">{doc.subject}</td>
                                        <td className="px-6 py-4 flex items-center space-x-3">
                                            <button
                                                onClick={() => navigate(`/letterheads/edit/${doc._id}`)} // Edit not fully implemented yet, assume re-use create logic
                                                className="text-blue-600 hover:text-blue-800" title="Edit"
                                            >
                                                <Edit className="w-4 h-4" />
                                            </button>
                                            <button
                                                onClick={() => navigate(`/letterheads/view/${doc._id}`)} // Assuming View logic or re-use create with readonly
                                                className="text-gray-600 hover:text-gray-800" title="Print"
                                            >
                                                <Printer className="w-4 h-4" />
                                            </button>
                                            <button
                                                onClick={() => handleDelete(doc._id)}
                                                className="text-red-500 hover:text-red-700" title="Delete"
                                            >
                                                <Trash2 className="w-4 h-4" />
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
        </Layout>
    );
};

export default LetterheadList;

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useSettings } from '../context/SettingsContext';
import Layout from '../components/Layout/Layout';
import api from '../utils/api';
import {
    Store, CreditCard, FileText, Smartphone,
    Save, Upload, Info, X, Image, Layout as LayoutIcon
} from 'lucide-react';
import toast from 'react-hot-toast';
import { QRCodeSVG } from 'qrcode.react';

const Settings = () => {
    const navigate = useNavigate();
    const { isAdmin } = useAuth();
    const { settings: currentSettings, refreshSettings, updateSettings } = useSettings();
    const [loading, setLoading] = useState(false);
    const [activeTab, setActiveTab] = useState('general');

    const [formData, setFormData] = useState({
        shopName: '',
        tagline: '',
        address: '',
        phone: '',
        email: '',
        gstNumber: '',
        bankName: '',
        accountNumber: '',
        ifscCode: '',
        bankBranch: '',
        upiId: '',
        termsAndConditions: '',
        brandColor: '#2563EB',
        primaryTextColor: '#2563EB',
        themeColor: '#2563EB',
        loginCardTextColor: '#FFFFFF',
        roleBadge: 'ADMIN'
    });

    useEffect(() => {
        if (!isAdmin()) {
            navigate('/dashboard');
            toast.error('Access denied. Admin only.');
            return;
        }
        if (currentSettings) {
            setFormData({
                shopName: currentSettings.shopName || '',
                tagline: currentSettings.tagline || '',
                address: currentSettings.address || '',
                phone: currentSettings.phone || '',
                email: currentSettings.email || '',
                gstNumber: currentSettings.gstNumber || '',
                bankName: currentSettings.bankName || '',
                accountNumber: currentSettings.accountNumber || '',
                ifscCode: currentSettings.ifscCode || '',
                bankBranch: currentSettings.bankBranch || '',
                upiId: currentSettings.upiId || '',
                termsAndConditions: currentSettings.termsAndConditions || '',
                brandColor: currentSettings.brandColor || '#2563EB',
                primaryTextColor: currentSettings.primaryTextColor || '#2563EB',
                themeColor: currentSettings.themeColor || '#2563EB',
                loginCardTextColor: currentSettings.loginCardTextColor || '#FFFFFF',
                roleBadge: currentSettings.roleBadge || 'ADMIN'
            });
        }
    }, [currentSettings]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const result = await updateSettings(formData);
            if (result.success) {
                toast.success('Settings updated successfully');
            } else {
                toast.error(result.error);
            }
        } catch (error) {
            toast.error('Failed to update settings');
        } finally {
            setLoading(false);
        }
    };

    const handleFileUpload = async (e, field) => {
        const file = e.target.files[0];
        if (!file) return;

        const formDataUpload = new FormData();
        formDataUpload.append(field, file);

        try {
            await api.put('/settings', formDataUpload, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            toast.success(`${field} uploaded successfully`);
            refreshSettings();
        } catch (error) {
            toast.error(`Failed to upload ${field}`);
        }
    };

    const handleRemoveFile = async (field) => {
        if (!window.confirm('Are you sure you want to remove this image?')) return;

        try {
            await api.put('/settings', { [field]: null });
            toast.success('Image removed successfully');
            refreshSettings();
        } catch (error) {
            toast.error('Failed to remove image');
        }
    };

    const tabs = [
        { id: 'general', name: 'General', icon: Store },
        { id: 'banking', name: 'Banking & QR', icon: CreditCard },
        { id: 'invoice', name: 'Invoice Design', icon: FileText },
        { id: 'appearance', name: 'App Appearance', icon: Smartphone },
        { id: 'letterheadTemplate', name: 'Letterhead Design', icon: LayoutIcon },
    ];

    const colorPresets = [
        '#2563EB', '#10B981', '#EF4444', '#8B5CF6',
        '#F59E0B', '#06B6D4', '#F97316', '#EC4899'
    ];

    const darkColorPresets = [
        '#1F2937', '#374151', '#4B5563', '#6B7280',
        '#1E40AF', '#047857', '#B91C1C', '#5B21B6'
    ];

    return (
        <Layout>
            <div className="p-6">
                {/* Header */}
                <div className="flex items-center justify-between mb-6">
                    <div className="flex items-center space-x-3">
                        <Store className="w-8 h-8 text-blue-600" />
                        <div>
                            <h1 className="text-3xl font-bold text-gray-900">Store Settings & Branding</h1>
                            <p className="text-gray-600 mt-1">Customize your store information and app appearance</p>
                        </div>
                    </div>
                    <button
                        onClick={handleSubmit}
                        disabled={loading}
                        className="flex items-center space-x-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors"
                    >
                        <Save className="w-5 h-5" />
                        <span>{loading ? 'Saving...' : 'Save All Changes'}</span>
                    </button>
                </div>

                {/* Demo Mode Indicator */}
                <div className="bg-orange-50 border-l-4 border-orange-400 p-4 mb-6 rounded-lg hidden">
                    <div className="flex items-center space-x-2">
                        <Info className="w-5 h-5 text-orange-600" />
                        <p className="text-sm text-orange-800">
                            <strong>Demo Mode:</strong> Settings saved to browser localStorage
                        </p>
                    </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    {/* Left Side - Tabs and Forms */}
                    <div className={activeTab === 'letterheadTemplate' ? "lg:col-span-3" : "lg:col-span-2"}>
                        {/* Tabs */}
                        <div className="bg-white rounded-xl shadow-sm mb-4">
                            <div className="border-b border-gray-200">
                                <nav className="flex space-x-1 px-4" aria-label="Tabs">
                                    {tabs.map((tab) => {
                                        const Icon = tab.icon;
                                        return (
                                            <button
                                                key={tab.id}
                                                onClick={() => setActiveTab(tab.id)}
                                                className={`flex items-center space-x-2 py-4 px-4 border-b-2 font-medium text-sm transition-colors ${activeTab === tab.id
                                                    ? 'border-blue-500 text-blue-600 bg-blue-50'
                                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:bg-gray-50'
                                                    }`}
                                            >
                                                <Icon className="w-5 h-5" />
                                                <span>{tab.name}</span>
                                            </button>
                                        );
                                    })}
                                </nav>
                            </div>
                        </div>

                        {/* Tab Content */}
                        <form onSubmit={handleSubmit}>
                            <div className="bg-white rounded-xl shadow-sm p-6">
                                {/* General Tab */}
                                {activeTab === 'general' && (
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Store Name *
                                            </label>
                                            <input
                                                type="text"
                                                value={formData.shopName}
                                                onChange={(e) => setFormData({ ...formData, shopName: e.target.value })}
                                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                required
                                            />
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Tagline (Optional)
                                            </label>
                                            <input
                                                type="text"
                                                value={formData.tagline}
                                                onChange={(e) => setFormData({ ...formData, tagline: e.target.value })}
                                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                placeholder="e.g., Best Deals in Tech"
                                            />
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Address
                                            </label>
                                            <textarea
                                                value={formData.address}
                                                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                rows={3}
                                                placeholder="Shop No. 12, Ground Floor&#10;Tech Plaza, MG Road&#10;Indore - 452001, MP"
                                            />
                                        </div>

                                        <div className="grid grid-cols-2 gap-4">
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                                    Mobile Number
                                                </label>
                                                <input
                                                    type="tel"
                                                    value={formData.phone}
                                                    onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                    placeholder="+91 98765 43210"
                                                />
                                            </div>

                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                                    Email
                                                </label>
                                                <input
                                                    type="email"
                                                    value={formData.email}
                                                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                    placeholder="contact@yourbusiness.com"
                                                />
                                            </div>
                                        </div>

                                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                                    GSTIN (Optional)
                                                </label>
                                                <input
                                                    type="text"
                                                    value={formData.gstNumber}
                                                    onChange={(e) => setFormData({ ...formData, gstNumber: e.target.value })}
                                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                    placeholder="e.g., 22AAAAA0000A1Z5"
                                                />
                                            </div>
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                                    Website (Optional)
                                                </label>
                                                <input
                                                    type="text"
                                                    value={formData.website || ''}
                                                    onChange={(e) => setFormData({ ...formData, website: e.target.value })}
                                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                    placeholder="www.yourbusiness.com"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                )}

                                {/* Banking & QR Tab */}
                                {activeTab === 'banking' && (
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Bank Details (Multi-line)
                                            </label>
                                            <textarea
                                                defaultValue={`Bank Name: ${formData.bankName}\nAccount No.: ${formData.accountNumber}\nIFSC Code: ${formData.ifscCode}\nBranch: ${formData.bankBranch}`}
                                                onBlur={(e) => {
                                                    const lines = e.target.value.split('\n');
                                                    setFormData({
                                                        ...formData,
                                                        bankName: lines[0]?.replace(/^Bank Name:\s*/, '').trim() || '',
                                                        accountNumber: lines[1]?.replace(/^Account No\.?:\s*/, '').trim() || '',
                                                        ifscCode: lines[2]?.replace(/^IFSC Code:\s*/, '').trim() || '',
                                                        bankBranch: lines[3]?.replace(/^Branch:\s*/, '').trim() || ''
                                                    });
                                                }}
                                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 font-mono text-sm"
                                                rows={4}
                                                placeholder="Bank Name: HDFC Bank&#10;Account No.: 1234567890&#10;IFSC Code: HDFC0001234&#10;Branch: MG Road, Indore"
                                            />
                                            <p className="text-xs text-gray-500 mt-1">
                                                This will appear at the bottom of your invoices. Edit freely and click outside to save changes.
                                            </p>
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                UPI ID (for QR Code)
                                            </label>
                                            <input
                                                type="text"
                                                value={formData.upiId}
                                                onChange={(e) => setFormData({ ...formData, upiId: e.target.value })}
                                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                                placeholder="yourstore@okicici"
                                            />
                                            <p className="text-xs text-gray-500 mt-1">
                                                QR code will be automatically generated on invoices
                                            </p>
                                        </div>

                                        {/* QR Code Preview */}
                                        {formData.upiId && (
                                            <div className="mt-4 p-4 bg-gray-50 rounded-lg border border-gray-200">
                                                <label className="block text-sm font-medium text-gray-700 mb-3">
                                                    UPI QR Code Preview
                                                </label>
                                                <div className="flex items-start space-x-4">
                                                    <div className="bg-white p-3 rounded-lg shadow-sm">
                                                        <QRCodeSVG
                                                            value={`upi://pay?pa=${formData.upiId}&pn=${encodeURIComponent(formData.shopName || 'Store')}`}
                                                            size={128}
                                                            level="M"
                                                            includeMargin={true}
                                                        />
                                                    </div>
                                                    <div className="flex-1">
                                                        <p className="text-sm text-gray-700 font-medium mb-2">
                                                            Scan to Pay: {formData.shopName || 'Your Store'}
                                                        </p>
                                                        <p className="text-xs text-gray-600 mb-1">
                                                            UPI ID: <span className="font-mono">{formData.upiId}</span>
                                                        </p>
                                                        <p className="text-xs text-green-600 mt-3">
                                                            Γ£ô This QR code will appear on all invoices
                                                        </p>
                                                    </div>
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                )}

                                {/* Invoice Design Tab */}
                                {activeTab === 'invoice' && (
                                    <div className="space-y-6">
                                        <div className="bg-blue-50 border-l-4 border-blue-400 p-4 rounded-lg">
                                            <p className="text-sm text-blue-800">
                                                <strong>Customize Your Invoice Design</strong>
                                                <br />
                                                Control colors and branding that appear on all invoices
                                            </p>
                                        </div>

                                        {/* Brand Color */}
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Brand Color (Invoice Theme)
                                            </label>
                                            <div className="flex items-center space-x-3">
                                                <input
                                                    type="color"
                                                    value={formData.brandColor}
                                                    onChange={(e) => setFormData({ ...formData, brandColor: e.target.value })}
                                                    className="w-16 h-10 rounded cursor-pointer"
                                                />
                                                <input
                                                    type="text"
                                                    value={formData.brandColor}
                                                    onChange={(e) => setFormData({ ...formData, brandColor: e.target.value })}
                                                    className="px-3 py-2 border border-gray-300 rounded-lg w-32"
                                                />
                                            </div>
                                            <p className="text-xs text-gray-500 mt-2">
                                                Used for invoice header, table headers, and GRAND TOTAL badge
                                            </p>
                                            <div className="flex flex-wrap gap-2 mt-3">
                                                <p className="text-xs text-gray-600 w-full">Quick picks:</p>
                                                {colorPresets.map((color) => (
                                                    <button
                                                        key={color}
                                                        type="button"
                                                        onClick={() => setFormData({ ...formData, brandColor: color })}
                                                        className="w-8 h-8 rounded border-2 border-gray-300 hover:border-gray-500 transition"
                                                        style={{ backgroundColor: color }}
                                                    />
                                                ))}
                                            </div>
                                        </div>

                                        {/* Company Logo */}
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Company Logo
                                            </label>
                                            <div className="flex items-center space-x-4">
                                                {currentSettings?.logo && (
                                                    <img
                                                        src={currentSettings.logo.startsWith('http') ? currentSettings.logo : `http://localhost:5000${currentSettings.logo}`}
                                                        alt="Logo"
                                                        className="w-16 h-16 object-contain border border-gray-200 rounded-lg p-2"
                                                    />
                                                )}
                                                <label className="flex items-center space-x-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 cursor-pointer transition-colors">
                                                    <Upload className="w-5 h-5" />
                                                    <span>Upload Logo</span>
                                                    <input
                                                        type="file"
                                                        onChange={(e) => handleFileUpload(e, 'logo')}
                                                        accept="image/*"
                                                        className="hidden"
                                                    />
                                                </label>
                                                {currentSettings?.logo && (
                                                    <button
                                                        onClick={() => handleRemoveFile('logo')}
                                                        className="p-2 text-red-500 hover:bg-red-50 rounded-lg transition-colors border border-red-200"
                                                        title="Remove Logo"
                                                    >
                                                        <X className="w-5 h-5" />
                                                    </button>
                                                )}
                                            </div>
                                            <p className="text-xs text-gray-500 mt-2">
                                                PNG, JPG, WEBP (max 2MB). Appears in invoice header.
                                            </p>
                                        </div>

                                        {/* Letterhead Upload */}


                                        {/* Primary Text Color */}
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Primary Text Color (for Invoice)
                                            </label>
                                            <div className="flex items-center space-x-3">
                                                <input
                                                    type="color"
                                                    value={formData.primaryTextColor}
                                                    onChange={(e) => setFormData({ ...formData, primaryTextColor: e.target.value })}
                                                    className="w-16 h-10 rounded cursor-pointer"
                                                />
                                                <input
                                                    type="text"
                                                    value={formData.primaryTextColor}
                                                    onChange={(e) => setFormData({ ...formData, primaryTextColor: e.target.value })}
                                                    className="px-3 py-2 border border-gray-300 rounded-lg w-32"
                                                />
                                            </div>
                                            <p className="text-xs text-gray-500 mt-2">
                                                Applied to invoice text elements. Choose dark colors for better readability.
                                            </p>
                                            <div className="flex flex-wrap gap-2 mt-3">
                                                <p className="text-xs text-gray-600 w-full">Quick picks:</p>
                                                {darkColorPresets.map((color) => (
                                                    <button
                                                        key={color}
                                                        type="button"
                                                        onClick={() => setFormData({ ...formData, primaryTextColor: color })}
                                                        className="w-8 h-8 rounded border-2 border-gray-300 hover:border-gray-500 transition"
                                                        style={{ backgroundColor: color }}
                                                    />
                                                ))}
                                            </div>
                                        </div>

                                        {/* Digital Signature */}
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Digital Signature / Stamp
                                            </label>
                                            <div className="flex items-center space-x-4">
                                                {currentSettings?.digitalSignature ? (
                                                    <img
                                                        src={currentSettings.digitalSignature.startsWith('http') ? currentSettings.digitalSignature : `http://localhost:5000${currentSettings.digitalSignature}`}
                                                        alt="Signature"
                                                        className="w-24 h-16 object-contain border border-gray-200 rounded-lg p-2"
                                                    />
                                                ) : (
                                                    <div className="w-24 h-16 border border-gray-200 rounded-lg flex items-center justify-center bg-gray-50 text-gray-400">
                                                        <span className="font-serif italic text-lg">Signed</span>
                                                    </div>
                                                )}
                                                <label className="flex items-center space-x-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 cursor-pointer transition-colors">
                                                    <Upload className="w-5 h-5" />
                                                    <span>Upload Signature</span>
                                                    <input
                                                        type="file"
                                                        onChange={(e) => handleFileUpload(e, 'digitalSignature')}
                                                        accept="image/*"
                                                        className="hidden"
                                                    />
                                                </label>
                                                {currentSettings?.digitalSignature && (
                                                    <button
                                                        onClick={() => handleRemoveFile('digitalSignature')}
                                                        className="p-2 text-red-500 hover:bg-red-50 rounded-lg transition-colors border border-red-200"
                                                        title="Remove Signature"
                                                    >
                                                        <X className="w-5 h-5" />
                                                    </button>
                                                )}
                                            </div>
                                            <p className="text-xs text-gray-500 mt-2">
                                                PNG with transparency recommended. Appears in invoice footer.
                                            </p>
                                        </div>

                                        {/* Invoice Text Configuration */}
                                        <div className="border-t border-gray-200 pt-6 mt-6">
                                            <h4 className="text-md font-medium text-gray-800 mb-4">Invoice Text & Labels</h4>

                                            <div className="space-y-4">
                                                <div>
                                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                                        Terms & Conditions
                                                    </label>
                                                    <textarea
                                                        value={formData.termsAndConditions || ''}
                                                        onChange={(e) => setFormData({ ...formData, termsAndConditions: e.target.value })}
                                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                                                        rows={3}
                                                        placeholder="e.g. Goods once sold will not be taken back."
                                                    />
                                                </div>

                                                <div>
                                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                                        Footer Note
                                                    </label>
                                                    <input
                                                        type="text"
                                                        value={formData.invoiceFooterText || ''}
                                                        onChange={(e) => setFormData({ ...formData, invoiceFooterText: e.target.value })}
                                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                                                        placeholder="e.g. Thank you for your business!"
                                                    />
                                                </div>

                                                <div>
                                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                                        Authorized Signatory Label
                                                    </label>
                                                    <input
                                                        type="text"
                                                        value={formData.authSignLabel || ''}
                                                        onChange={(e) => setFormData({ ...formData, authSignLabel: e.target.value })}
                                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                                                        placeholder="e.g. Authorized Signatory"
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}


                                {/* App Appearance Tab */}
                                {activeTab === 'appearance' && (
                                    <div className="space-y-6">
                                        <div className="bg-purple-50 border-l-4 border-purple-400 p-4 rounded-lg">
                                            <p className="text-sm text-purple-800">
                                                <strong>Customize App Theme</strong>
                                                <br />
                                                Personalize the look and feel of your application
                                            </p>
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Primary Theme Color
                                            </label>
                                            <div className="flex items-center space-x-3">
                                                <input
                                                    type="color"
                                                    value={formData.themeColor}
                                                    onChange={(e) => setFormData({ ...formData, themeColor: e.target.value })}
                                                    className="w-16 h-10 rounded cursor-pointer"
                                                />
                                                <input
                                                    type="text"
                                                    value={formData.themeColor}
