import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Layout from '../components/Layout/Layout';
import api from '../utils/api';
import { formatINR } from '../utils/currency';
import { formatDate } from '../utils/date';
import { ArrowLeft, RefreshCw, Printer, Plus, X } from 'lucide-react';
import toast from 'react-hot-toast';

const SupplierLedger = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [supplier, setSupplier] = useState(null);
    const [ledger, setLedger] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showPaymentModal, setShowPaymentModal] = useState(false);
    const [showPurchaseModal, setShowPurchaseModal] = useState(false);
    const [payment, setPayment] = useState({
        amount: '',
        method: 'Cash',
        notes: ''
    });
    const [purchase, setPurchase] = useState({
        amount: '',
        billNo: '',
        notes: '',
        billFile: null
    });

    useEffect(() => {
        loadData();
    }, [id]);

    const loadData = async () => {
        try {
            setLoading(true);
            const [supplierRes, ledgerRes] = await Promise.all([
                api.get(`/suppliers/${id}`),
                api.get(`/supplier-ledger/${id}`)
            ]);
            setSupplier(supplierRes.data);
            setLedger(ledgerRes.data);
            setLoading(false);
        } catch (error) {
            console.error('Error loading supplier ledger:', error);
            toast.error('Failed to load supplier ledger');
            setLoading(false);
        }
    };

    const handleRecalculate = async () => {
        try {
            await api.post(`/supplier-ledger/recalculate/${id}`);
            toast.success('Ledger recalculated');
            loadData();
        } catch (error) {
            toast.error('Recalculation failed');
        }
    };

    const handleRecordPayment = async () => {
        if (!payment.amount || payment.amount <= 0) {
            return toast.error('Please enter a valid amount');
        }

        try {
            await api.post('/payments/supplier', {
                supplierId: id,
                amount: parseFloat(payment.amount),
                method: payment.method,
                notes: payment.notes || 'Payment to supplier'
            });

            toast.success('Payment recorded successfully');
            setShowPaymentModal(false);
            setPayment({ amount: '', method: 'Cash', notes: '' });
            loadData();
        } catch (error) {
            console.error('Payment error:', error);
            toast.error(error.response?.data?.message || 'Failed to record payment');
        }
    };

    const handleRecordPurchase = async () => {
        if (!purchase.amount || purchase.amount <= 0) {
            return toast.error('Please enter a valid amount');
        }

        try {
            const formData = new FormData();
            formData.append('supplierId', id);
            formData.append('amount', parseFloat(purchase.amount));
            formData.append('billNo', purchase.billNo || '');
            formData.append('notes', purchase.notes || 'Purchase recorded');
            if (purchase.billFile) {
                formData.append('billFile', purchase.billFile);
            }

            await api.post('/supplier-ledger/record-purchase', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            toast.success('Purchase recorded successfully');
            setShowPurchaseModal(false);
            setPurchase({ amount: '', billNo: '', notes: '', billFile: null });
            loadData();
        } catch (error) {
            console.error('Purchase error:', error);
            toast.error(error.response?.data?.message || 'Failed to record purchase');
        }
    };

    return (
        <Layout>
            <div className="p-6">
                {/* Header */}
                <div className="flex items-center justify-between mb-6">
                    <div className="flex items-center space-x-4">
                        <button
                            onClick={() => navigate('/suppliers')}
                            className="p-2 hover:bg-gray-100 rounded-lg"
                        >
                            <ArrowLeft className="w-6 h-6 text-gray-600" />
                        </button>
                        <div>
                            <h1 className="text-2xl font-bold text-gray-900">
                                {loading ? 'Loading...' : `${supplier?.name} - Purchase Ledger`}
                            </h1>
                            <p className="text-gray-600">
                                {supplier?.phone} | {supplier?.address}
                            </p>
                        </div>
                    </div>

                    <div className="flex items-center space-x-3">
                        <button
                            onClick={() => setShowPurchaseModal(true)}
                            className="flex items-center space-x-2 px-4 py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700"
                        >
                            <Plus className="w-4 h-4" />
                            <span>Record Purchase</span>
                        </button>
                        <button
                            onClick={() => setShowPaymentModal(true)}
                            className="flex items-center space-x-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                        >
                            <Plus className="w-4 h-4" />
                            <span>Record Payment</span>
                        </button>
                        <button
                            onClick={handleRecalculate}
                            className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 bg-white"
                        >
                            <RefreshCw className="w-4 h-4" />
                            <span>Recalculate</span>
                        </button>
                        <button className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                            <Printer className="w-4 h-4" />
                            <span>Print Statement</span>
                        </button>
                    </div>
                </div>

                {/* Summary Cards */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                    <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
                        <p className="text-sm text-gray-500 mb-1">Total Credit (Purchases)</p>
                        <p className="text-2xl font-bold text-red-600 rupee">
                            {formatINR(ledger.reduce((sum, entry) => sum + (entry.credit || 0), 0))}
                        </p>
                    </div>
                    <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
                        <p className="text-sm text-gray-500 mb-1">Total Debit (Paid)</p>
                        <p className="text-2xl font-bold text-green-600 rupee">
                            {formatINR(ledger.reduce((sum, entry) => sum + (entry.debit || 0), 0))}
                        </p>
                    </div>
                    <div className="bg-orange-50 p-6 rounded-xl shadow-sm border border-orange-100">
                        <p className="text-sm text-orange-800 mb-1">Net Amount Owed</p>
                        <p className="text-3xl font-bold text-orange-700 rupee">
                            {formatINR(supplier?.balance || 0)}
                        </p>
                    </div>
                </div>

                {/* Ledger Table */}
                <div className="bg-white rounded-xl shadow-sm overflow-hidden">
                    <div className="overflow-x-auto">
                        <table className="w-full">
                            <thead>
                                <tr className="bg-gray-50 border-b border-gray-200">
                                    <th className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">Date</th>
                                    <th className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">Particulars</th>
                                    <th className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">Ref No</th>
                                    <th className="px-6 py-4 text-right text-xs font-semibold text-gray-500 uppercase tracking-wider text-green-600">Debit (Dr)</th>
                                    <th className="px-6 py-4 text-right text-xs font-semibold text-gray-500 uppercase tracking-wider text-red-600">Credit (Cr)</th>
                                    <th className="px-6 py-4 text-right text-xs font-semibold text-gray-500 uppercase tracking-wider">Balance</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y divide-gray-200">
                                {loading ? (
                                    <tr>
                                        <td colSpan="6" className="text-center py-8">Loading ledger...</td>
                                    </tr>
                                ) : ledger.length === 0 ? (
                                    <tr>
                                        <td colSpan="6" className="text-center py-8 text-gray-500">No transactions found</td>
                                    </tr>
                                ) : (
                                    ledger.map((entry) => (
                                        <tr key={entry._id} className="hover:bg-gray-50 transition-colors">
                                            <td className="px-6 py-4 text-sm text-gray-900 whitespace-nowrap">
                                                {formatDate(entry.date)}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-900">
                                                <div className="font-medium">{entry.refType}</div>
                                                <div className="text-gray-500 text-xs">{entry.description}</div>
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-500 font-mono">
                                                {entry.refNo || '-'}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-right font-medium text-green-600">
                                                {entry.debit > 0 ? formatINR(entry.debit) : '-'}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-right font-medium text-red-600">
                                                {entry.credit > 0 ? formatINR(entry.credit) : '-'}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-right font-bold text-gray-900 rupee bg-gray-50">
                                                {formatINR(entry.balance)}
                                            </td>
                                        </tr>
                                    ))
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Payment Recording Modal */}
            {showPaymentModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white rounded-lg max-w-md w-full p-6">
                        <h3 className="text-xl font-bold text-gray-900 mb-4">Record Payment to Supplier</h3>

                        <div className="space-y-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Amount *
                                </label>
                                <input
                                    type="number"
                                    value={payment.amount}
                                    onChange={(e) => setPayment({ ...payment, amount: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
                                    placeholder="Enter amount paid"
                                    min="0"
                                    step="0.01"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Payment Method *
                                </label>
                                <select
                                    value={payment.method}
                                    onChange={(e) => setPayment({ ...payment, method: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
                                >
                                    <option value="Cash">Cash</option>
                                    <option value="Credit">Credit</option>
                                    <option value="UPI">UPI</option>
                                    <option value="Card">Card</option>
                                    <option value="Bank Transfer">Bank Transfer</option>
                                    <option value="Cheque">Cheque</option>
                                </select>
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Notes (Optional)
                                </label>
                                <textarea
                                    value={payment.notes}
                                    onChange={(e) => setPayment({ ...payment, notes: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
                                    rows="3"
                                    placeholder="e.g., Payment for Bill #123"
                                ></textarea>
                            </div>
                        </div>

                        <div className="flex justify-end space-x-3 mt-6">
                            <button
                                onClick={() => setShowPaymentModal(false)}
                                className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handleRecordPayment}
                                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 font-medium"
                            >
                                Record Payment
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Purchase Recording Modal */}
            {showPurchaseModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white rounded-lg max-w-md w-full p-6">
                        <h3 className="text-xl font-bold text-gray-900 mb-4">Record Purchase</h3>

                        <div className="space-y-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Amount *
                                </label>
                                <input
                                    type="number"
                                    value={purchase.amount}
                                    onChange={(e) => setPurchase({ ...purchase, amount: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                                    placeholder="Enter purchase amount"
                                    min="0"
                                    step="0.01"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Bill Number (Optional)
                                </label>
                                <input
                                    type="text"
                                    value={purchase.billNo}
                                    onChange={(e) => setPurchase({ ...purchase, billNo: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                                    placeholder="e.g., BILL-123"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Notes (Optional)
                                </label>
                                <textarea
                                    value={purchase.notes}
                                    onChange={(e) => setPurchase({ ...purchase, notes: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                                    rows="3"
                                    placeholder="e.g., 10 boxes of LED bulbs"
                                ></textarea>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Bill Attachment (Optional)
                                    </label>
                                    {!purchase.billFile ? (
                                        <>
                                            <input
                                                type="file"
                                                accept="image/*,.pdf"
                                                onChange={(e) => setPurchase({ ...purchase, billFile: e.target.files[0] })}
                                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                                            />
                                            <p className="text-xs text-gray-500 mt-1">Upload bill image or PDF (max 5MB)</p>
                                        </>
                                    ) : (
                                        <div className="flex items-center justify-between p-2 border border-orange-200 bg-orange-50 rounded-lg">
                                            <div className="flex items-center space-x-2 overflow-hidden">
                                                <span className="text-sm font-medium text-orange-800">File selected:</span>
                                                <span className="text-sm text-gray-700 truncate">{purchase.billFile.name}</span>
                                            </div>
                                            <button
                                                type="button"
                                                onClick={() => setPurchase({ ...purchase, billFile: null })}
                                                className="text-red-500 hover:bg-red-50 p-1 rounded-full transition-colors"
                                                title="Remove File"
                                            >
                                                <X className="w-5 h-5" />
                                            </button>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        <div className="flex justify-end space-x-3 mt-6">
                            <button
                                onClick={() => setShowPurchaseModal(false)}
                                className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handleRecordPurchase}
                                className="px-4 py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700 font-medium"
                            >
                                Record Purchase
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </Layout>
    );
};

export default SupplierLedger;

